INSERT INTO insults VALUES ('', 'I married your mother because I wanted children, imagine my disappointment \r\nwhen you came along.');
INSERT INTO insults VALUES ('', 'Actually, I never liked Dylan''s kind of music before; I always thought he \r\nsounded just like Yogi Bear.');
