INSERT INTO jokes VALUES (1538, 'Bird jokes', 'What flies through the jungle singing opera ? \r\n\r\nThe parrots of Penzance !');
INSERT INTO jokes VALUES (1539, 'Bird jokes', 'What kind of bird opens doors ? \r\nA kiwi !');
INSERT INTO jokes VALUES (1540, 'Bird jokes', 'What is a duck''s favorite TV show ? \r\nThe \r\nfeather forecast !');
INSERT INTO jokes VALUES (1541, 'Bird jokes', 'What do you get if you cross a parrot with a \r\n\r\nshark ? \r\nA bird that will talk you ear off !');
INSERT INTO jokes VALUES (1542, 'Bird jokes', 'What do you call a bird that \r\nlives \r\nunderground ? \r\nA mynah bird !');
INSERT INTO jokes VALUES (1543, 'Bird jokes', 'Where do birds invest their money ? \r\nIn the \r\nstork market !');
