INSERT INTO jokes VALUES (1549, 'Bird jokes', 'What do you get if your budgie flies into the \r\n\r\nblender ? \r\nShredded Tweet !');
INSERT INTO jokes VALUES (1550, 'Bird jokes', 'What did they call the canary that flew into the \r\n\r\npastry dish ? \r\nTweetie Pie !');
INSERT INTO jokes VALUES (1551, 'Bird jokes', 'What kind of birds do you usually find locked up \r\n? \r\nJail-birds !');
