INSERT INTO jokes VALUES (1553, 'Bird jokes', 'What is the definition of Robin ? \r\nA bird who \r\nsteals !');
INSERT INTO jokes VALUES (1554, 'Bird jokes', 'Why did the chicken cross the basketball court \r\n?\r\nHe heard the referee calling fowls');
