INSERT INTO jokes VALUES (1557, 'Bird jokes', 'Why did the chicken cross the \r\nplayground \r\n?\r\nTo get to the other slide');
INSERT INTO jokes VALUES (1558, 'Bird jokes', 'Why did the chicken cross the "net" ?\r\nIt \r\nwanted to get to the other site !');
INSERT INTO jokes VALUES (1559, 'Bird jokes', 'What do you call a rooster who \r\nwakes you up \r\nat the same time every morning ?\r\nAn alarm cluck !');
INSERT INTO jokes VALUES (1560, 'Bird jokes', 'Why does a chicken coop have two doors \r\n?\r\nBecause if had four doors it would be a chicken sedan!');
