INSERT INTO jokes VALUES (1562, 'Bird jokes', 'Why did the chicken end up in the soup \r\n?\r\nBecause it ran out of cluck !');
INSERT INTO jokes VALUES (1563, 'Bird jokes', 'What do you call a crazy chicken ?\r\nA cuckoo \r\ncluck !');
INSERT INTO jokes VALUES (1564, 'Bird jokes', 'What happened to the chicken whose feathers were \r\nall \r\npointing the wrong way ? \r\nShe was tickled to death !');
