INSERT INTO jokes VALUES (1569, 'Bird jokes', 'What happened when the chicken ate cement \r\n?\r\nShe laid a sidewalk !');
INSERT INTO jokes VALUES (1570, 'Bird jokes', 'What did the chicken do when he saw a bucket of \r\n\r\nfried chicken ? \r\nShe kicked the bucket !');
INSERT INTO jokes VALUES (1571, 'Bird jokes', 'Why did the chicken cross the road half way \r\n?\r\nHe wanted to lay it on the line !');
