INSERT INTO jokes VALUES (1573, 'Bird jokes', 'What did one chicken say to the other after they \r\n\r\nwalked through poison ivy ?\r\n"You scratch my beak and I''ll \r\nscratch yours !"');
INSERT INTO jokes VALUES (1574, 'Bird jokes', 'Why did the chick \r\ndisappoint his mother ? \r\n\r\nHe wasn''t what he was cracked up to be !');
INSERT INTO jokes VALUES (1575, 'Bird jokes', 'Is chicken soup good for \r\nyour health ?\r\nNot \r\nif you''re the chicken !');
INSERT INTO jokes VALUES (1576, 'Bird jokes', 'What do chickens serve at birthday \r\nparties \r\n?\r\nCoop-cakes !');
INSERT INTO jokes VALUES (1577, 'Bird jokes', 'What do you get when a chicken lays an egg on top \r\nof a \r\nbarn ?\r\nAn eggroll !');
INSERT INTO jokes VALUES (1578, 'Bird jokes', 'What do you call the outside of a hand gren-egg \r\n?\r\nThe bombshell !');
