INSERT INTO jokes VALUES (2328, 'Bus jokes', 'Have you \r\nheard that all the buses and trains \r\nare stopping today? \r\nNo. Is there a strike? \r\nNo, they''re stopping \r\nto let the passengers off.');
INSERT INTO jokes VALUES (2329, 'Bus jokes', 'What have I got in \r\nmy hands? \r\nA double \r\ndecker bus! \r\nYou looked!');
