INSERT INTO jokes VALUES (2951, 'Christmas jokes', 'Why does Santa Claus only have seven \r\n\r\nreindeer? \r\nBecause Prancer moved in with a hairdresser in Beverly \r\nHills.');
INSERT INTO jokes VALUES (2952, 'Christmas jokes', 'Why is \r\nChristmas just like a day at the \r\noffice ?\r\nYou do all the work and the fat guy with the suit gets all \r\nthe \r\ncredit.');
