INSERT INTO jokes VALUES (2954, 'Clinton jokes', 'Q: How can you tell Bill Clinton from a bunch \r\n\r\nof dead bodies?\r\nA: He''s the stiff one.');
INSERT INTO jokes VALUES (2955, 'Clinton jokes', 'Q: How many Bill Clintons does it take to \r\n\r\nchange a lightbulb?\r\nA: Two--One to promise he''ll do it better than \r\nanyone else and one to \r\nobscure the issues.');
INSERT INTO jokes VALUES (2956, 'Clinton jokes', 'Q: How many Bill Clintons does it take to \r\nchange \r\na lightbulb?\r\nA: None--He''ll only promise "change."');
INSERT INTO jokes VALUES (2957, 'Clinton jokes', 'Q: How many Bill Clintons \r\ndoes it take to \r\nchange a lightbulb?\r\nA: He doesn''t! He whines a while, says "I feel \r\nyour pain", and gets \r\ncongress to pass a billion dollar light \r\nsecurity bill, and blames \r\nRepublicans and special interests for not \r\nmaking lightbulbs free.');
INSERT INTO jokes VALUES (2958, 'Clinton jokes', 'Q: Why \r\nare people in Arkansas having \r\npeanut butter and jelly for Thanksgiving \r\nthis year?\r\nA: Because \r\nthey''re sending their turkey to the White House!');
INSERT INTO jokes VALUES (2959, 'Clinton jokes', 'Q: Why are people in Arkansas having peanut \r\n\r\nbutter and jelly for Thanksgiving this year?\r\nA: Because they \r\ncan''t afford any more pork.');
