INSERT INTO jokes VALUES (3130, 'Cow jokes', 'Did you hear about the farmer who \r\nlost control \r\nof his tractor in the cow pasture? \r\nNo! Did he hurt the cows? \r\n\r\nNo, he just grazed them!');
