INSERT INTO jokes VALUES (3142, 'Cow jokes', 'I can''t decide whether to buy a \r\nbicycle or a \r\ncow for my farm. \r\nWell, wouldn''t you look silly riding a cow? \r\n\r\nI''d look a darn sight sillier trying to milk a bicycle!');
