INSERT INTO jokes VALUES (3236, 'Cow jokes', 'What''s a cow''s favorite moosical \r\nnote?\r\nBeef-flat!');
INSERT INTO jokes VALUES (3237, 'Cow jokes', 'What''s a moo hoo for a bunch of weirdo cattle? \r\n\r\nA nerd herd!');
INSERT INTO jokes VALUES (3238, 'Cow jokes', 'What''s a moo hoo for a cattle dinner? \r\nCow \r\nchow!');
INSERT INTO jokes VALUES (3239, 'Cow jokes', 'What''s a moo hoo for a cow barn on a holiday? \r\n\r\nA merry dairy!');
INSERT INTO jokes VALUES (3240, 'Cow jokes', 'What''s a moo hoo for a darling bull? \r\nA dear \r\nsteer!');
INSERT INTO jokes VALUES (3241, 'Cow jokes', 'What''s a moo hoo for a stuffed steer? \r\nA full \r\nbull!');
INSERT INTO jokes VALUES (3242, 'Cow jokes', 'What''s a moo hoo for a tug-of-war between two \r\n\r\nlonghorns? \r\nA bull pull!');
INSERT INTO jokes VALUES (3243, 'Cow jokes', 'What''s a moo hoo for a young calf? \r\nA new \r\nmoo!');
INSERT INTO jokes VALUES (3244, 'Cow jokes', 'What''s a moo hoo for grazing school? \r\nGrass \r\nclass!');
INSERT INTO jokes VALUES (3245, 'Cow jokes', 'What''s a moo hoo for the sound you hear when a \r\ncow \r\nspits? \r\nA cud thud!');
INSERT INTO jokes VALUES (3246, 'Cow jokes', 'When a bull wants to listen to a cassette, what \r\ndoes he \r\nput on his head? \r\nSteer phones!');
INSERT INTO jokes VALUES (3247, 'Cow jokes', 'When is a farmer like a magician?\r\nWhen he turns \r\nhis cow into pasture.');
INSERT INTO jokes VALUES (3248, 'Cow jokes', 'Where did the bull carry his stock-market report? \r\n\r\nIn his beef case!');
INSERT INTO jokes VALUES (3249, 'Cow jokes', 'Where did the mooron take the baby cow to eat? \r\n\r\nTo the calf-ateria!');
INSERT INTO jokes VALUES (3250, 'Cow jokes', 'Where do cows like to live? St. Moo-is.');
INSERT INTO jokes VALUES (3251, 'Cow jokes', 'Where \r\ndo cows like to ride on trains?\r\nIn \r\nthe cow-boose.');
INSERT INTO jokes VALUES (3252, 'Cow jokes', 'Where do Danish cows come from? \r\n\r\nCowpenhagenf');
INSERT INTO jokes VALUES (3253, 'Cow jokes', 'Where do milk shakes come from?\r\nNervous \r\ncows!');
INSERT INTO jokes VALUES (3254, 'Cow jokes', 'Where do Russian cows come from? \r\nMoscow!');
INSERT INTO jokes VALUES (3255, 'Cow jokes', 'Where do steers go to dance? \r\nTo the Meat \r\nBall!');
INSERT INTO jokes VALUES (3256, 'Cow jokes', 'Where does a cow stop to drink? \r\nThe milky \r\nway!');
INSERT INTO jokes VALUES (3257, 'Cow jokes', 'Why are cpws made for dancing? \r\nThey''re all \r\nborn hoofers!');
INSERT INTO jokes VALUES (3258, 'Cow jokes', 'Why couldn''t the cow leave the farm? \r\nShe was \r\npasteurized!');
INSERT INTO jokes VALUES (3259, 'Cow jokes', 'Why did Bossy slug Roy Rogers? \r\nShe heard he \r\nwas a cowpuncher!');
INSERT INTO jokes VALUES (3260, 'Cow jokes', 'Why did Bossy tell the cowpoke to \r\nleave her \r\ncalf alone? \r\nShe thought children should be seen and not herded!');
INSERT INTO jokes VALUES (3261, 'Cow jokes', 'Why did the cow \r\njump over the moon? \r\nTo get \r\nto the Milky Way!');
INSERT INTO jokes VALUES (3262, 'Cow jokes', 'Why did the farmer feed money to his cow? \r\nHe \r\nwanted rich milk!');
INSERT INTO jokes VALUES (3263, 'Cow jokes', 'Why did the farmer fence in the bull? \r\nThe \r\nfarmer had too much of a steak in him to let him go!');
INSERT INTO jokes VALUES (3264, 'Cow jokes', 'Why did the \r\nfarmer put brandy in the cow''s \r\nfood?\r\nHe wanted to raised stewed beef!');
INSERT INTO jokes VALUES (3265, 'Cow jokes', 'Why did the farmer put his cow on \r\nthe \r\nscales?\r\nHe wanted to see how much the milky weighed!');
INSERT INTO jokes VALUES (3266, 'Cow jokes', 'Why did the moron give \r\nthe sleepy cow a \r\nhammer? \r\nHe wanted her to hit the hay!');
INSERT INTO jokes VALUES (3267, 'Cow jokes', 'Why do cows think cooks are mean? \r\nThey whip \r\ncream!');
INSERT INTO jokes VALUES (3268, 'Cow jokes', 'Why doesn''t Sweden export it''s cattle?\r\nIt \r\nwants to keep it''s Stockholm!');
INSERT INTO jokes VALUES (3269, 'Cow jokes', 'Why don''t cows ever have any \r\nmoney? \r\n\r\nBecause the farmers milk them dry!');
INSERT INTO jokes VALUES (3270, 'Cow jokes', 'Why was he woman arrested on a \r\ncattle ranch \r\nfor wearing a silk dress?\r\nShe was charged with rustling!');
INSERT INTO jokes VALUES (3271, 'Cow jokes', 'Why was the calf afraid?\r\nHe was a cow-herd!');
INSERT INTO jokes VALUES (3272, 'Cow jokes', 'Why wouldn''t anyone play with the little \r\n\r\nlonghorn? \r\nHe was too much of a bully!');
INSERT INTO jokes VALUES (3273, 'Cow jokes', 'Your Honor, it was an accident! I had to run into \r\n\r\nthe fence to keep from hitting the cow! \r\nWas it a Jersey cow? \r\n\r\nI don''t know, I didn''t see her license plate!');
INSERT INTO jokes VALUES (3274, 'Cow jokes', 'What is a cow''s \r\nfavourite TV show ? \r\nDr \r\nMoo !');
INSERT INTO jokes VALUES (3275, 'Cow jokes', 'If you had fifteen cows and five goats what would \r\nyou have ? \r\nPlenty of milk !');
INSERT INTO jokes VALUES (3276, 'Cow jokes', 'What''s the best way to make a bull sweat ? \r\n\r\nPut him in a tight jumper !');
INSERT INTO jokes VALUES (3277, 'Cow jokes', 'Why do cows like being told jokes ? \r\nBecause \r\nthey like being amoosed !');
INSERT INTO jokes VALUES (3278, 'Cow jokes', 'What do you get if you cross a cow with an octopus \r\n\r\n? \r\nA cow that can milk itself !');
INSERT INTO jokes VALUES (3279, 'Cow jokes', 'Q. What does a cow make when the sun \r\ncomes \r\nout? \r\nA. A shadow');
INSERT INTO jokes VALUES (3280, 'Cow jokes', 'What do you get when a cow goes to the Beach with \r\ntanning \r\noil? \r\nPre-tanned leather.');
INSERT INTO jokes VALUES (3281, 'Cowboy jokes', 'Cowboy Joe was telling his fellow cowboys back \r\n\r\non the ranch about his \r\nfirst visit to a big-city church. "When \r\nI got there, they had me park \r\nmy \r\nold truck in the corral," Joe \r\nbegan. \r\n"You mean the parking lot," interrupted Charlie, a more \r\nworldly \r\nfellow. \r\n"I walked up the trail to the door," Joe continued. \r\n\r\n"The sidewalk to the door," Charlie corrected him. \r\n"Inside the \r\ndoor, I was met by this dude," Joe went on. \r\n"That would be the \r\nusher," Charlie explained. \r\n"Well, the usher led me down the chute," Joe \r\nsaid. \r\n"You mean the aisle," Charlie said. \r\n"Then, he led me to a \r\nstall and told me to sit there," Joe continued. \r\n"Pew," Charlie \r\nretorted. \r\n"Yeah," recalled Joe. "That''s what that pretty lady said \r\nwhen I sat \r\ndown \r\nbeside her."');
INSERT INTO jokes VALUES (3282, 'Cowboy jokes', 'Who do zombie cowboys fight? \r\nDeadskins.');
INSERT INTO jokes VALUES (3283, 'Cowboy jokes', 'What did the cowboy maggot say when he went \r\ninto the \r\nsaloon bar? \r\nGimme a slug of whiskey.');
INSERT INTO jokes VALUES (3284, 'Cowboy jokes', 'Who is in cowboy films and is always broke? \r\n\r\nSkint Eastwood.');
INSERT INTO jokes VALUES (3285, 'Cowboy jokes', 'What do you call a frog who wants to be a \r\ncowboy? \r\nHoppalong Cassidy.');
INSERT INTO jokes VALUES (3286, 'Cowboy jokes', 'A police officer saw a man dressed as a cowboy \r\n\r\nin the street, complete with huge stetson hat, spurs, and six \r\nshooters.\r\n"Excuse me, sir," said the police officer, "who are \r\nyou?"\r\n"My name''s Tex, officer," said the cowboy.\r\n" eh?" said the police \r\nofficer, "Are you from Texas?" \r\n"Nope, Louisiana." \r\n"Louisiana? So \r\nwhy are you called Tex?" \r\n"Don''t want to be called Louise, do I .');
INSERT INTO jokes VALUES (3287, 'Cowboy jokes', 'Visitor: Wow, you have a \r\nlot of flies \r\nbuzzing round your horses and cows. Do you ever shoo them? \r\nCowboy: No we \r\njust let them go barefoot.');
INSERT INTO jokes VALUES (3288, 'Cowboy jokes', 'The swing doors of the Wild \r\nWest saloon \r\ncrashed open and in came Little Pete, black with fury. \r\n"All right!" he \r\nraged, "all right! Who did it? What goldarned \r\nvarmint painted my \r\nhorse blue?" \r\nThe huge figure of Black Jake, notorious gunfighter and \r\ntown baddie \r\nrose from a chair by the door. \r\n"It was me, shrimp," \r\nhe drawled, bunching his gigantic fists, "what \r\nabout it?" \r\n"Oh, \r\nwell, er," stammered little Pete wretchedly, "all I wanted to \r\nsay \r\nwas. . .when are you going to give it another coat?"');
INSERT INTO jokes VALUES (3289, 'Cowboy jokes', 'Who do \r\nzombie cowboys fight? \r\n\r\nDeadskins.');
INSERT INTO jokes VALUES (3290, 'Cowboy jokes', 'Swint and Fess, two Oklahoma cowboys, were \r\nresting their \r\nhorses out on the range. \r\n\r\n"What''d Emmaline give \r\nyew for yore birthday?" asked Swint. \r\n\r\n"Pair of cufflinks," said \r\nFess. "But I ain''t got no use for them. I \r\ncan''t even find anyplace \r\nto get my wrists pierced."');
INSERT INTO jokes VALUES (3291, 'Cowboy jokes', 'Back in the Old West three Texas cowboys were \r\n\r\nabout to be hung for cattle rustling. The lynch mob brought the \r\nthree men \r\nto a tree right at the edge of the Rio Grande. The idea was \r\nthat when \r\neach man had died, they''d cut the rope and he''d drop \r\ninto the river \r\nand drift out of sight \r\n\r\nThey put the first \r\ncowboy in the noose, but he was so sweaty and greasy \r\nhe slipped out, \r\nfell in the river and swam to freedom. \r\n\r\nThey tied the noose around \r\nthe second cowboy''s head. He, too, oozed \r\nout of the rope, dropped \r\ninto the river and got away. \r\n\r\nAs they dragged the third Texan to \r\nthe scaffold, he resisted, "Please! \r\nWould yaw''l tighten that noose \r\na little bit? I can''t swim!"');
INSERT INTO jokes VALUES (3292, 'Cowboy jokes', 'Q: Why \r\ncan''t the bankrupt cowboy \r\ncomplain?\r\nA: He has got no beef.');
INSERT INTO jokes VALUES (3293, 'Cowboy jokes', 'The cowboy lay sprawled across three entire \r\n\r\nseats in the posh Amarillo theatre. When the usher came by and noticed \r\n\r\nthis he whispered to the cowboy, "Sorry, sir, but you''re only \r\nallowed \r\none seat." The cowboy groaned but didn''t budge. The usher \r\nbecame more \r\nimpatient. "Sir, if you don''t get up from there, I''m \r\ngoing to have \r\nto call the manager. The cowboy just groaned. \r\n\r\nThe \r\nusher marched briskly back up the aisle. In a moment he returned \r\n\r\nwith the manager. Together the two of them tried repeatedly to move the \r\n\r\ncowboy, but with no success. Finally, they summoned the police. The \r\ncop \r\nsurveyed the situation briefly then asked, "All right buddy, \r\nwhat''s \r\nyou''re name?" \r\n\r\n"Sam," the cowboy moaned. \r\n\r\n\r\n"Where ya from, Sam?" \r\n\r\nWith pain in his voice Sam replied.... "The \r\nbalcony."');
INSERT INTO jokes VALUES (3294, 'Cowboy jokes', 'Three cowboys \r\nof the world are sitting \r\naround camp talking about how tough they were \r\nand the tales kept \r\ngetting bigger and bigger. \r\nThe cowboy from Australia says, "I wrestled a \r\n200 pound crocodile and \r\nmay it cry like a baby." \r\n\r\nThe Cowboy \r\nfrom Brazil shakes his head and says, "I killed a 400 pound \r\nsteer \r\nwith my bare hands." \r\n\r\nThe Cowboy from Texas just smiled and kept \r\nstirring the campfire with \r\nhis leg.');
INSERT INTO jokes VALUES (3295, 'Cowboy jokes', 'The eastern lady who was all ready to take a \r\nhorseback ride \r\nsaid to the cowboy, "Can you get me a nice gentle \r\npony?" "Shore," \r\nsaid the cowboy. "What kind of a saddle do you want, \r\nEnglish or \r\nwestern?" "What''s the difference?" asked the lady. "The \r\nwestern saddle has \r\na horn on it," said the cowboy. "If the traffic \r\nis so thick here in \r\nthe mountains that I need a horn on my saddle, I \r\ndon''t believe I want \r\nto ride."');
INSERT INTO jokes VALUES (3296, 'Cowboy jokes', 'Why did the cowboy ride his horse?\r\nBecause \r\nthe horse was too heavy to carry.');
INSERT INTO jokes VALUES (3297, 'Cowboy jokes', 'What sickness do cowboys \r\nget from riding \r\nwild horses?\r\nBronchitis (bronc-itis).');
INSERT INTO jokes VALUES (3298, 'Cowboy jokes', 'Why was the cowboy a lot of laughs?\r\nHe was \r\nalways horsing around.');
INSERT INTO jokes VALUES (3299, 'Cowboy jokes', 'Why did the cowboy get a hot seat?\r\nBecause \r\nhe rode the range.');
INSERT INTO jokes VALUES (3300, 'Cowboy jokes', 'Why did the cowboy''s car stop?\r\nIt had Injun \r\n(engine) trouble.');
INSERT INTO jokes VALUES (3301, 'Cowboy jokes', 'What do you call a cowboy who helps out in a \r\n\r\nschool ?\r\nThe deputy head !');
INSERT INTO jokes VALUES (3302, 'Cowboy jokes', 'If a cowboy rides into town on Friday and three \r\n\r\ndays later leaves on Friday, how does he do it? \r\nThe horse''s \r\nname is Friday!');
INSERT INTO jokes VALUES (3303, 'Cowboy jokes', 'Q: Why did the bowlegged cowboy get \r\nfired? \r\n\r\n\r\nA: Because he couldn''t keep his calves together!');
INSERT INTO jokes VALUES (3304, 'Cowboy jokes', 'What advice to cows \r\ngive?\r\nTurn the udder \r\ncheek and mooooove on!');
INSERT INTO jokes VALUES (3305, 'Cowboy jokes', 'Where do cowboys cook their \r\nmeals?\r\nOn \r\nthe range.');
INSERT INTO jokes VALUES (3306, 'Criminal jokes', 'A shoplifter was caught red-handed trying to \r\n\r\nsteal a watch from an exclusive jewelry store. "Listen," said the \r\n\r\nshoplifter, "I know you don''t want any trouble either. What do you \r\nsay I \r\njust buy the watch, and we forget about this?"\r\n\r\nThe \r\nmanager agreed and wrote up the sales slip. The crook looked at the \r\n\r\nslip and said, "This is a little more than I intended to spend. Can \r\n\r\nyou show me something less expensive?"');
INSERT INTO jokes VALUES (3307, 'Criminal jokes', 'A computer geek goes to prison \r\nfor fraud, \r\nthey put him in a cell with a 300LB guy, \r\n\r\nHaving heard what \r\nhappens to geeks in prison and being nervous he \r\nfigures he had better \r\nintroduce himself, He extends his hand and says with a \r\nquivering \r\nvoice, Hi my name is John Smith.\r\n\r\nThe big guy who actually is a nice \r\nguy extends his and says my name is \r\nTurner Brown.\r\n\r\nThe geek \r\npasses out. \r\n\r\nThe big guy fans him and brings him too. \r\n\r\nWhy \r\ndid you pass out he asked? \r\n\r\nThe geek replies, what did you say \r\nyour name was? \r\n\r\nTurner Brown he replies. \r\n\r\nOh God the geek \r\nsays I thought you said "TURN AROUND".');
INSERT INTO jokes VALUES (3308, 'Criminal jokes', 'It was \r\nRocky''s first night in the \r\npenitentiary. All of the inmates were in\r\ntheir cells and he was trying to \r\nbecome a bit more comfortable with his\r\nmeager surroundings. As he \r\nleaned against the bars at the front of his\r\ncell, Rocky heard a voice \r\ncall out "44" and the whole cell block \r\nerupted\r\ninto laughter! \r\nAnother voice called "16" and again there was \r\nlaughter. A\r\nthird \r\nvoice called "62" which was followed by laughter throughout the \r\n\r\nblock.\r\n\r\nRocky didn''t know what was going on so he rapped on his cell \r\nwall.\r\n\r\n"Yeah, whaddaya want?" came the gruff reply from next \r\ndoor.\r\n\r\n"What''s going on, here?" asked Rocky.\r\n\r\n"Well," said the \r\nother inmate, "down in the prison library there''s \r\nonly one\r\njoke \r\nbook. We''ve all read the book so many times that we don''t waste \r\n\r\ntime\r\ntelling the joke, we just call out it''s number."\r\n\r\nSo the \r\nnext day Rocky went down to the library and, sure enough, found \r\n r\nthe\r\nyellowed, dog-eared joke book and read it from cover to \r\ncover. That \r\nnight,\r\nwanting to be part of the group, Rocky \r\nconfidently called out "44" \r\nand\r\neveryone laughed! He tried calling "16" \r\nand "62" and again there \r\nwere peals\r\nof laughter. Then he called \r\n57, and the halls rang with laughter.\r\n\r\nAfter several minutes, one \r\nprisoner was still rolling on the floor \r\nlaughing.\r\nMore minutes - \r\nstill laughing. Rocky rapped on the cell wall. "Yeah,\r\nwaddaya want?" \r\nasked the other inmate.\r\n\r\n"I don''t understand it," asked Rocky, \r\n"Why is Bill still \r\nlaughing?"\r\n\r\n"Well," said the gruff inmate, \r\n"He never heard that one \r\nbefore!"');
INSERT INTO jokes VALUES (3309, 'Criminal jokes', 'Several years ago, Andy was sentenced to \r\nprison. During his stay, he \r\ngot along well with the guards and all his \r\nfellow inmates. The warden saw \r\nthat deep down, Andy was a good \r\nperson and made arrangements for Andy \r\nto learn a trade while doing his \r\ntime. After three years, Andy was \r\nrecognized as one of the best \r\ncarpenters in the local area. Often he would \r\nbe given a weekend pass \r\nto do odd jobs for the citizens of the \r\ncommunity.... and he always \r\nreported back to prison before Sunday night was \r\nover.\r\n\r\nThe \r\nwarden was thinking of remodeling his kitchen and in fact had done \r\n\r\nmuch of the work himself. But he lacked the skills to build a set of \r\n\r\nkitchen cupboards and a large counter top, which he had promised his \r\n\r\nwife. So he called Andy into his office and asked him to complete the \r\njob \r\nfor him.\r\n\r\nBut, alas, Andy refused. He told the warden, \r\nGosh, I''d really like to \r\nhelp you but counter fitting is what go\r\n t me into prison in the first \r\nplace.');
INSERT INTO jokes VALUES (3310, 'Criminal jokes', 'Newsflash: Two criminals have escaped from \r\nprison today. One is \r\norange and 9ft tall, and the other green and \r\nyellow and 2ft Gin tall. \r\nThe police are searching high and low for \r\nthem.');
INSERT INTO jokes VALUES (3311, 'Criminal jokes', 'Did you hear about the burglar who fell in \r\nthe \r\ncement mixer? \r\nNow he''s a hardened criminal.');
INSERT INTO jokes VALUES (3312, 'Criminal jokes', 'When the school was broken into, the \r\n\r\nthieves took absolutely everything - desks, books, blackboards, \r\n\r\neverything apart from the soap in the lavatories and all the towels. \r\nThe \r\npolice are looking for a pair of dirty criminals.');
INSERT INTO jokes VALUES (3313, 'Criminal jokes', 'What''s another \r\nword for a murderer who \r\nkills old ladies? \r\nA Killergran.');
INSERT INTO jokes VALUES (3314, 'Criminal jokes', 'Two small time thieves had been sent by the \r\nBig Boss to \r\nsteal a van load of goods from a bathroom suppliers. One \r\nstayed in the \r\nvan as look out and the other went into the \r\nstoreroom. Fifteen minutes \r\nwent by, then half an hour, then an hour, and no \r\nsign of him. The look \r\nout finally grew impatient and went to look \r\nfor his partner. Inside the \r\nstore the two came face to face. "Where \r\nhave you been?" demanded the \r\nworried look out. "The boss told me to \r\ntake a bath, but I couldn''t \r\nfind the soap and a towel."');
INSERT INTO jokes VALUES (3315, 'Criminal jokes', 'The criminal mastermind found one of his \r\n\r\ngang sawing the legs off his bed. "What are you doing that for?" \r\n\r\ndemanded the crook boss. "Only doing what you ordered," said the stupid \r\n\r\nthug. "You told me to lie low for a bit!"');
INSERT INTO jokes VALUES (3316, 'Criminal jokes', 'Why was the robber bionic? \r\nHe was holding \r\nup a bank.');
INSERT INTO jokes VALUES (3317, 'Criminal jokes', '"Dad," said Fred to his father, who was a \r\n\r\nbank robber. "I need $50 for the school trip tomorrow." \r\n"OK, son," \r\nsaid his dad, "I''ll get you the cash when the bank \r\ncloses."');
INSERT INTO jokes VALUES (3318, 'Criminal jokes', 'A stupid bank robber rushed into a bank, \r\npointed two fingers at \r\nthe clerk and said, "This is a muck up!" \r\n\r\n"Don''t you mean a stick up?" asked the girl. \r\n"No," said the robber, \r\n"it''s a muckup. I''ve forgotten my \r\ngun."');
INSERT INTO jokes VALUES (3319, 'Criminal jokes', 'A woman woke her husband in the middle of the \r\nnight. "There''s a \r\nburglar downstairs eating the cake that I made \r\nthis morning." \r\n"Who shall I call," her husband asked, "police or \r\n\r\nambulance?"');
INSERT INTO jokes VALUES (3320, 'Criminal jokes', 'Fred: We had a burglary last night, and they \r\ntook everything except the \r\nsoap and towels. \r\nHarry: The dirty \r\ncrooks.');
INSERT INTO jokes VALUES (3321, 'Criminal jokes', 'Why did the burglar take a shower? \r\nHe \r\nwanted to make a clean getaway');
INSERT INTO jokes VALUES (3322, 'Criminal jokes', 'What did the burglar say to the \r\n\r\nwatchmaker as he tied him up? \r\nSorry to take so much of your valuable \r\ntime.');
INSERT INTO jokes VALUES (3323, 'Criminal jokes', 'Judge: Why did you steal \r\nthat bird? \r\n\r\nPrisoner: For a lark, sir.');
INSERT INTO jokes VALUES (3324, 'Criminal jokes', 'Judge: You claim you robbed the grocery \r\n\r\nstore because you were starving. So why didn''t you take the food \r\ninstead \r\nof the cash out of the till? \r\nBurglar: Your Honour! I''m a \r\nproud man, sir, and I make it a rule to \r\npay for everything I eat.');
INSERT INTO jokes VALUES (3325, 'Criminal jokes', 'I think I hear burglars, dear. Are you awake? \r\n\r\nNo!');
INSERT INTO jokes VALUES (3326, 'Criminal jokes', 'Judge: Tell me your occupation. \r\nPrisoner; \r\nI''m a locksmith, Your Honour. \r\nJudge: Then what were you doing in a \r\njewellery shop in the middle of \r\nthe night when the police saw you? \r\n\r\nPrisoner; Making a bolt for the door!');
INSERT INTO jokes VALUES (3327, 'Criminal jokes', '"What makes you think the \r\nprisoner was \r\ndrunk?" asked the judge. "Well, Your Honor," replied the \r\narresting \r\nofficer, "I saw him lift up a manhole cover and walk away with \r\nit, \r\nand when I asked him what it was for he said, ''I want to listen to \r\n\r\nit on my record-player!'' "');
INSERT INTO jokes VALUES (3328, 'Criminal jokes', 'A police officer was escorting a \r\nprisoner \r\nto jail when his hat blew off. \r\n"Shall I run and get it for you?" \r\nasked the prisoner obligingly. \r\n"You must think I''m daft," said the \r\nofficer. \r\n"You stand here and I''ll get it."');
INSERT INTO jokes VALUES (3329, 'Criminal jokes', 'Why was the robber so secure? \r\nHe was a \r\nsafe robber.');
INSERT INTO jokes VALUES (3330, 'Criminal jokes', 'Victim (to mugger): But my watch isn''t any \r\n\r\ngood, it only has sentimental value.\r\nMugger: That''s all right. \r\nI''m sentimental.');
INSERT INTO jokes VALUES (3331, 'Criminal jokes', 'Criminal: Why don''t you hire these twins for \r\n\r\nthe robbery, boss?\r\nCriminal Boss: I''m afraid of a \r\ndouble-cross.');
INSERT INTO jokes VALUES (3332, 'Criminal jokes', 'Detective: Do you think \r\nI should put on \r\nthe cuffs?\r\nCriminal: Why? You look good in short sleeves.');
INSERT INTO jokes VALUES (3333, 'Criminal jokes', 'Detective: How did you \r\nget into \r\ncounterfeiting?\r\nCriminal: I answered an ad that said, "Make money at \r\nhome."');
INSERT INTO jokes VALUES (3334, 'Criminal jokes', 'Detective: Why did you dump those vegetables \r\non my desk?\r\nCriminal: You said it was time to spill the beans.');
INSERT INTO jokes VALUES (3335, 'Criminal jokes', 'Policeman: Did you \r\nknow your vehicle was \r\nreported stolen?\r\nCriminal: It wasn''t when I took it.');
INSERT INTO jokes VALUES (3336, 'Criminal jokes', 'A boy is in a prison cell with no windows and \r\n\r\nno doors: there are no holes in the ceiling or trapdoors in the \r\nfloor, \r\nyet in the morning the jailers find him gone. How did he get \r\nout ?\r\nThrough the doorway - there were no doors remember !');
INSERT INTO jokes VALUES (3337, 'Criminal jokes', '''It''s a pity \r\nyou''ve gone on hunger \r\nstrike,'' said the convict''s girlfriend on \r\nvisiting day. ''Why ?'' \r\n''I''ve put a file in your cake.''');
INSERT INTO jokes VALUES (3338, 'Criminal jokes', 'What did the \r\nburglar say to the lady who \r\ncaught him stealing her silver? \r\nI''m at your service, ma''am.');
INSERT INTO jokes VALUES (3339, 'Criminal jokes', 'How do bank robbers send messages?\r\nBy flee \r\nmail!');
INSERT INTO jokes VALUES (3340, 'Criminal jokes', 'What hired killer never goes to jail?\r\nThe \r\nexterminator.');
INSERT INTO jokes VALUES (3341, 'Criminal jokes', 'What kind of thief steals meat?\r\nA \r\nhamburglar.');
INSERT INTO jokes VALUES (3342, 'Criminal jokes', 'Who is the strongest thief?\r\nA \r\nshoplifter.');
INSERT INTO jokes VALUES (3343, 'Criminal jokes', 'If you crossed a gangster and a garbage man, \r\nwhat would \r\nyou have?\r\nOrganised grime (crime).');
INSERT INTO jokes VALUES (3344, 'Criminal jokes', 'What diploma do criminals get?\r\nThe third \r\ndegree.');
INSERT INTO jokes VALUES (3345, 'Criminal jokes', 'What is the difference between a thief and a \r\nchurch \r\nbell?\r\nOne steals from the people, the other peals, from \r\nthe steeple.');
INSERT INTO jokes VALUES (3346, 'Criminal jokes', 'What kind of robbery is not dangerous?\r\nA \r\nsafe robbery.');
INSERT INTO jokes VALUES (3347, 'Criminal jokes', 'What three letters in the alphabet frighten \r\n\r\ncriminals?\r\nF.B.I.');
INSERT INTO jokes VALUES (3348, 'Criminal jokes', 'When did the criminal get smart?\r\nWhen the \r\njudge threw the book at him.');
INSERT INTO jokes VALUES (3349, 'Criminal jokes', 'Why is a sinking ship like a \r\nperson in \r\njail?\r\nBecause it needs bailing out.');
INSERT INTO jokes VALUES (3350, 'Criminal jokes', 'What kind of party do prisoners in jail \r\n\r\nlike most of all.\r\nA going-away party.');
INSERT INTO jokes VALUES (3351, 'Criminal jokes', 'What stars go to jail?\r\nShooting stars.');
INSERT INTO jokes VALUES (3352, 'Criminal jokes', 'Why would someone in jail want to catch the \r\nmeasles? \r\nSo he could break out.');
INSERT INTO jokes VALUES (3353, 'Criminal jokes', 'Who was the world''s greatest thief \r\n?\r\nAtlas, because he held up the whole world !');
INSERT INTO jokes VALUES (3354, 'Criminal jokes', 'Why do pens get sent to \r\nprison ? To do \r\nlong sentences !');
INSERT INTO jokes VALUES (3355, 'Criminal jokes', 'What was the parrot doing in prison ?\r\nIt \r\nwas a jail-bird !');
INSERT INTO jokes VALUES (3356, 'Criminal jokes', 'Why are burglars such good tennis players \r\n?\r\nBecause they spend such a lot of their time in courts !');
INSERT INTO jokes VALUES (3357, 'Criminal jokes', 'Who is the \r\nbiggest gangster in the sea \r\n?\r\nAl Caprawn !');
INSERT INTO jokes VALUES (3358, 'Criminal jokes', 'What do you call a robbery in China ?\r\nA \r\nChinese take away !');
INSERT INTO jokes VALUES (3359, 'Criminal jokes', 'What do you call a mayfly with a criminal \r\n\r\ntendencies ?\r\nBaddy long legs !');
INSERT INTO jokes VALUES (3360, 'Criminal jokes', 'What do you get if you cross a bunch of \r\nflowers \r\nwith a burglar ?\r\nRobbery with violets !');
INSERT INTO jokes VALUES (3361, 'Criminal jokes', 'What do you get if you cross a gorilla and a \r\n\r\nprisoner ?\r\nA Kong - vict !');
INSERT INTO jokes VALUES (3362, 'Criminal jokes', 'Knock Knock\r\nWho''s there !\r\nBurglar \r\n!\r\nBurglar who ?\r\nBurglars don''t knock !');
INSERT INTO jokes VALUES (3363, 'Criminal jokes', 't was Christmas and the judge was in a merry \r\n\r\nmood as he asked the prisoner, "What are you charged \r\nwith?"\r\n\r\n"Doing my Christmas shopping early," replied the \r\ndefendant.\r\n\r\n"That''s no offense," said the judge. "How early were you doing this \r\n\r\nshopping?"\r\n\r\n"Before the store opened," countered the prisoner.');
INSERT INTO jokes VALUES (3364, 'Criminal jokes', 'An English \r\nprisoner of war was held by \r\nthe Germans. The Englishman was shot all over \r\nthe place, and okay \r\nuntil one day when the German told him, \r\n"Englander,your arm is \r\ninfected with gangrene vee must cut it off." \r\n\r\nThe English prisoner \r\nsaid, "Well, okay, but could you drop it over \r\nEngland when you go \r\nbombing?" \r\n\r\nThe German replied, "Ya, that vill not be a problem." \r\n\r\n\r\nA few weeks later the German tells the Englishman that they have to \r\ncut \r\nhis other arm off. The Englishman says, "Well, could drop it \r\nover \r\nEngland like you did last time?"\r\n\r\n"Ya, that vill be done," \r\nsays the German. \r\n\r\nThe next day the German tells him that they \r\nhave to cut his leg off. \r\nOnce again the Brit says, "Well, could you \r\ndo the same as before?" \r\n\r\nThe German replies, "Vhy, ya." \r\n\r\n\r\nThe next the German tells him they have to cut his other leg. "Well," \r\n\r\nbegins the Brit, "could you just..." \r\n\r\nThe German snapped\r\n , "No! We think you are trying to escape!"');
INSERT INTO jokes VALUES (3365, 'Criminal jokes', 'A \r\nmafioso''s son sits at his desk writing \r\na Christmas list to Jesus. He first \r\nwrites, "Dear baby Jesus, I \r\nhave been a good boy the whole year, so I \r\nwant a new..." He looks at \r\nit, then crumples it up into a ball and \r\nthrows it away. \r\n\r\nHe \r\ngets out a new piece of paper and writes again, "Dear baby Jesus, I \r\n\r\nhave been a good boy for most of the year, so I want a new..." He \r\n\r\nagain looks at it with disgust and throws it away. \r\n\r\nHe then gets \r\nan idea. He goes into his mother''s room, takes a statue \r\nof the \r\nVirgin Mary, puts it in the closet, and locks the door. He takes \r\n\r\nanother piece of paper and writes, "Dear baby Jesus. If you ever want \r\nto \r\nsee your mother again..."');
INSERT INTO jokes VALUES (3366, 'Dance jokes', 'What''s a vampire''s favorite dance? \r\nThe \r\nFang Tango.');
INSERT INTO jokes VALUES (3367, 'Dance jokes', 'Q. Where can you dance in California? \r\nA. San \r\nFran-disco');
INSERT INTO jokes VALUES (3368, 'Dance jokes', 'Q. Why did the line dancer cross the dance \r\nfloor?\r\nA. To get to the other (Electric) Slide!');
INSERT INTO jokes VALUES (3369, 'Dance jokes', 'Two fonts walk into a line \r\ndance club. The \r\nbarman says to them, "Get out. We don''t serve your \r\ntype here."');
INSERT INTO jokes VALUES (3370, 'Dance jokes', 'How do they dance in Arabia?\r\nSheik-to-sheik \r\n(cheek).');
INSERT INTO jokes VALUES (3371, 'Criminal jokes', 'A rapist, a gangster and a murderer are in \r\nthe \r\nsame car... \r\nWho is driving the car?\r\nA police officer!');
INSERT INTO jokes VALUES (3372, 'Criminal jokes', 'Did you hear about the calendar thief?\r\nHe \r\ngot 12 months; they say his days are numbered!');
INSERT INTO jokes VALUES (3373, 'Criminal jokes', 'Bill: Where did you \r\nget that gold watch \r\nJoe?\r\nJoe: I won it in a race. \r\nBill: How many people participated \r\nin it? \r\nJoe: Three, a policeman, the owner of the watch, and me!!');
INSERT INTO jokes VALUES (3374, 'Criminal jokes', 'Late one \r\nnight, a burglar broke into a \r\nhouse he thought was empty. He tiptoed \r\nthrough the living room but \r\nsuddenly he froze in his tracks when he heard a \r\nloud voice say, \r\n"Jesus is watching you!" Silence returned to the \r\nhouse, so the burglar \r\ncrept forward again. "Jesus is watching you," the \r\nvoice boomed \r\nagain. The burglar stopped dead again. He was frightened.\r\nFrantically, he \r\nlooked all around. In a dark corner, he spotted a bird \r\ncage and in \r\nthe cage was a parrot. He asked the parrot: "Was that you \r\nwho said \r\nJesus is watching me?"\r\n"Yes," said the parrot.\r\nThe burglar \r\nbreathed a sigh of relief and asked the parrot: "What''s \r\nyour \r\nname?"\r\n"Clarence," said the bird.\r\n"That''s a dumb name for a parrot," \r\nsneered the burglar. "What idiot \r\nnamed you Clarence?"\r\nThe parrot said, \r\n"The same idiot who named the Rottweiller \r\nJesus."');
INSERT INTO jokes VALUES (3375, 'Dance jokes', 'What animals are poor dancers?\r\nFour-legged \r\nones, because they have two left feet.');
INSERT INTO jokes VALUES (3376, 'Dance jokes', 'What dance did the \r\nPilgrims do?\r\nThe \r\nPlymouth Rock.');
INSERT INTO jokes VALUES (3377, 'Dance jokes', 'What dance do hippies hate?\r\nA square \r\ndance.');
INSERT INTO jokes VALUES (3378, 'Dance jokes', 'What dance do you do when summer is \r\nover?\r\nTango (tan go).');
INSERT INTO jokes VALUES (3379, 'Dance jokes', 'What is the difference between a dancer and a \r\nduck?\r\nOne goes quick on her beautiful legs, the other goes quack on \r\nher \r\nbeautiful legs.');
INSERT INTO jokes VALUES (3380, 'Dance jokes', 'What kind of dance do buns do?\r\nAbundance.');
INSERT INTO jokes VALUES (3381, 'Dance jokes', 'Why did the little kid dance on the jar of \r\njam?\r\nBecause the top said, "Twist to open."');
INSERT INTO jokes VALUES (3382, 'Dance jokes', 'What did the overweight \r\nballet dancer \r\nperform ?\r\nThe dance of the sugar plump fairy !');
INSERT INTO jokes VALUES (3383, 'Dance jokes', 'What sort of dance do fish do at \r\nparties \r\n?\r\nThe conga !');
INSERT INTO jokes VALUES (3384, 'Dance jokes', 'What do you call someone who dances on cars \r\n?\r\nA morris dancer !');
INSERT INTO jokes VALUES (3385, 'Dance jokes', 'What do you get if you cross an insect and a \r\n\r\ndance ?\r\nA cricket ball !');
INSERT INTO jokes VALUES (3386, 'Dance jokes', 'Where can you dance in California? \r\nSan \r\nFrandisco.');
INSERT INTO jokes VALUES (3387, 'Dance jokes', 'I''ve been invited to an avoidance. \r\nAn \r\navoidance? What''s that? \r\nIt''s a dance for people who hate each \r\nother.');
INSERT INTO jokes VALUES (3388, 'Dance jokes', 'How do you make a \r\ntissue dance ?\r\nPut a \r\nlittle boogie in it !');
INSERT INTO jokes VALUES (3389, 'Dance jokes', 'Who is tall, dark and a great dancer ?\r\nDark \r\nRaver !');
INSERT INTO jokes VALUES (3390, 'Dance jokes', 'Q. What is good for your soul but not your \r\nsoles?\r\nA. Linedancing!');
INSERT INTO jokes VALUES (3391, 'Dance jokes', 'How do you see that a linedancer came from \r\nBelgium \r\nand not from the Netherlands?\r\nHe wears the cardboard box on \r\nhis boots.');
INSERT INTO jokes VALUES (3392, 'Dance jokes', 'An avid line dancing couple \r\ngo to the doctor \r\nfor a check up because they are having trouble \r\nremembering anything \r\nbut, all the latest line dances. The doctor finds them in \r\nexcellent \r\nhealth (of course), but suggests that writing things down may \r\nhelp \r\ntheir memories off the dance floor. That night the husband gets up \r\n\r\nto go the kitchen and the wife asks for a dish of ice cream, suggesting \r\n\r\nthat maybe he write it down. He says "I don''t need to write it \r\ndown" \r\nShe says "Well I want Strawberries on it, so maybe you better \r\nwrite it \r\ndown" "I don"t need to write it down" He says and walks off \r\nin a \r\nhuff. Twenty minutes later he comes back with a plate of bacon \r\nand eggs. \r\n"I told you to write it down" she says, "You forgot my \r\n\r\ntoast".');
INSERT INTO jokes VALUES (3393, 'Dance jokes', 'Which dances do the burgers do best?\r\nThe \r\nburger-loo and the char char!');
INSERT INTO jokes VALUES (3394, 'Dance jokes', 'Why don''t dogs make good dancers ?\r\nBecause \r\nthey have two left feet !');
INSERT INTO jokes VALUES (3395, 'Dance jokes', 'What do baby swans dance to ? \r\n\r\nCygnet-ure-tunes !');
INSERT INTO jokes VALUES (3396, 'Dance jokes', 'What''s a vampire''s favorite dance? \r\nThe \r\nVaults.');
INSERT INTO jokes VALUES (3397, 'Dance jokes', 'Why did the two knives go to the dance \r\ntogether?\r\n\r\nBecause they both looked sharp!');
INSERT INTO jokes VALUES (3398, 'Dance jokes', 'These two friends are about to go to \r\na club. \r\nOne of them has a wooden eye. He said ''''If someone says \r\nsomething \r\nabout my eye, i''m gonna snap.'''' They get there, and he asks a \r\n\r\ngirl to dance. She says, ''''Would I?''''');
INSERT INTO jokes VALUES (3399, 'Dance jokes', 'Q. What do you call a line \r\ndancer on a \r\ncruise?\r\nA. An Ocean "Liner"');
INSERT INTO jokes VALUES (3400, 'Dance jokes', 'There was a dance teacher who talked of a very \r\n\r\nold dance called the Politician. "All you have to do" she told her \r\n\r\nclass "is take three steps forward, two steps backward, then \r\nside-step \r\nside-step and turn around."');
INSERT INTO jokes VALUES (3401, 'Dance jokes', 'What is a frog''s favorite dance? \r\nThe Lindy \r\nHop.');
INSERT INTO jokes VALUES (3402, 'Dance jokes', 'What is a duck''s favorite dance ? \r\nThe \r\nquackstep !');
INSERT INTO jokes VALUES (3403, 'Dance jokes', 'Which dance will a chicken not do ? \r\nThe \r\nfoxtrot !');
INSERT INTO jokes VALUES (3404, 'Dance jokes', 'Q. What do tired line dancers do?\r\nA. They \r\nLine Down :-)');
INSERT INTO jokes VALUES (3405, 'Dance jokes', 'My dance partner dumped me for my best \r\nfriend.\r\nWhy? Was he a better dancer?\r\nDon''t know, I never met him.');
INSERT INTO jokes VALUES (3406, 'Dance jokes', 'What do cars do at the disco?\r\nBrake dance.');
INSERT INTO jokes VALUES (3407, 'Dance jokes', 'What do cows like to line dance to ?\r\nAny kind \r\nof moosic you like !');
INSERT INTO jokes VALUES (3408, 'Dance jokes', 'Q. What''s the difference between a \r\nline \r\ndance instructor and a dentist? \r\nA. A dentist lets you sit down while \r\nhe hurts you.');
INSERT INTO jokes VALUES (3409, 'Dance jokes', 'Q. How many line \r\ndance instructors does it \r\ntake to change a light bulb? \r\nA. Five!...Six!...Seven!...Eight!');
INSERT INTO jokes VALUES (3410, 'Dead and dying jokes', 'What lies on the ground 100 feet up in \r\n\r\nthe air and smells? \r\nA dead centipede.');
INSERT INTO jokes VALUES (3411, 'Dead and dying jokes', 'What is the difference between a \r\nmusician and a \r\ndead body? \r\nOne composes and the other decomposes.');
INSERT INTO jokes VALUES (3412, 'Dead and dying jokes', 'What has four legs, a tail, \r\n\r\nwhiskers and flies? \r\nA dead cat.');
INSERT INTO jokes VALUES (3413, 'Dead and dying jokes', 'What do you call a man who has been \r\ndead and buried for \r\nthousands of years? \r\nPete.');
INSERT INTO jokes VALUES (3414, 'Dead and dying jokes', 'What''s a zombie''s favorite pop song? \r\n\r\nDead sails in the sunset.');
INSERT INTO jokes VALUES (3415, 'Dead and dying jokes', 'What do you find in a zombie''s veins? \r\n\r\nDead blood corpuscles.');
INSERT INTO jokes VALUES (3416, 'Dead and dying jokes', 'Where do ghosts go for their holidays? \r\n\r\nThe Dead Sea.');
INSERT INTO jokes VALUES (3417, 'Dead and dying jokes', 'First ghoul: You don''t look too well \r\ntoday. \r\nSecond ghoul: No, I''m dead on my feet.');
INSERT INTO jokes VALUES (3418, 'Dead and dying jokes', 'Did you hear about the man \r\nwho left \r\nhis job at the mortuary? \r\nIt was a dead end job.');
INSERT INTO jokes VALUES (3419, 'Dead and dying jokes', 'Did you hear about the two men who were \r\n\r\ncremated at the same time? \r\nIt was a dead heat.');
INSERT INTO jokes VALUES (3420, 'Dead and dying jokes', 'If a man was born in England, raised in \r\n\r\nAmerica and died in Spain, what does that make him? \r\nDead.');
INSERT INTO jokes VALUES (3421, 'Dead and dying jokes', 'Doctor, doctor, I feel dead from the \r\nwaist down. \r\nI''ll arrange for you to be halfburied.');
INSERT INTO jokes VALUES (3422, 'Dead and dying jokes', 'Waiter, waiter! There''s a \r\ndead fly \r\nin my soup. \r\nOh no! Who''s going to look after his family?');
INSERT INTO jokes VALUES (3423, 'Dead and dying jokes', 'Why did the monster \r\ntake a dead man \r\nfor a drive in his car? \r\nBecause he was a car-case.');
INSERT INTO jokes VALUES (3424, 'Dead and dying jokes', 'Teacher: If I had ten flies on my desk, \r\n\r\nand I swatted one, how many flies would be left?\r\nGirl: One - the \r\ndead one!');
INSERT INTO jokes VALUES (3425, 'Dead and dying jokes', 'Did you hear about the do-it-yourself \r\n\r\nfuneral? \r\nThey just loosen the earth and you sink down by \r\nyourself.');
INSERT INTO jokes VALUES (3426, 'Dead and dying jokes', 'Did you \r\nhear about the undertaker \r\nwho buried someone in the wrong place and was \r\nsacked for the grave \r\nmistake?');
INSERT INTO jokes VALUES (3427, 'Dead and dying jokes', 'Why do you want to be buried at sea? \r\n\r\nBecause my wife says she wants to dance on my grave.');
INSERT INTO jokes VALUES (3428, 'Dead and dying jokes', 'I was so sorry \r\nto hear you buried \r\nyour mother last week.\r\nWell, we had to, you know, she was dead.');
INSERT INTO jokes VALUES (3429, 'Dead and dying jokes', 'A monster and a zombie went \r\ninto \r\nthe undertaker''s. "I''d like to order a coffin for a friend of \r\nmine \r\nwho has just died," said the monster. "Certainly, sir," said the \r\n\r\nundertaker, "but there was really no need to bring him with you."');
INSERT INTO jokes VALUES (3430, 'Dead and dying jokes', 'Did you hear someone has invented a \r\n\r\ncoffin that just covers the head?\r\nIt''s for people like you who''re \r\ndead from the neck up!');
INSERT INTO jokes VALUES (3431, 'Dead and dying jokes', 'The man who \r\nwas about to die said \r\nto the Sheriff, \r\n"Say, do I really have to die swinging from a tree?" \r\n\r\n"Course not," replied the Sheriff. \r\n"We just put the rope round \r\nyour neck and kick the horse away. After \r\nthat it''s up to you."');
INSERT INTO jokes VALUES (3432, 'Dead and dying jokes', 'A chemist, a shopkeeper and a teacher \r\nwere \r\nsentenced to death by firing squad. The chemist was taken from \r\nhis cell \r\nand as the soldiers took aim he shouted "Avalanche!" The \r\nsoldiers \r\npanicked and in the confusion the chemist escaped. The \r\nshopkeeper was led \r\nout next. As the soldiers took aim he shouted \r\n"Flood!" and escaped. The \r\nteacher was then lead out. The squad took aim \r\nand the teacher, \r\nremenbering how the other two had escaped, shouted \r\n"Fire!"');
INSERT INTO jokes VALUES (3433, 'Dead and dying jokes', 'At the inquest \r\ninto her husband''s \r\ndeath by food poisoning Mrs Wally was asked by the \r\ncoroner if she \r\ncould remember her husband''s last words. \r\n"Yes," she replied. "He \r\nsaid ''I don''t know how that shop can make \r\na profit from selling \r\nthis salmon at only 20 cents a tin..."');
INSERT INTO jokes VALUES (3434, 'Dead and dying jokes', 'A man \r\nis calling on his best friend \r\nto pay a condolence call the day after the \r\nfriend''s wife has died. \r\n\r\n\r\nWhen he knocks on the door, he gets no answer, so he decides to \r\ngo in \r\nand see if everything is all right. Upon entering the house, \r\nthe man \r\ndiscovers his friend in the living room kissing a \r\nmate.\r\n\r\n"Jack", says the man, "Your wife just died yesterday!!" His friend \r\n\r\nlooks up and says, "In this grief, do you think I know what I''m \r\n\r\ndoing?"');
INSERT INTO jokes VALUES (3435, 'Dead and dying jokes', '"Why are you crying Fred?" asked the \r\n\r\nteacher. "\r\n''Cos my parrot died last night. I washed it in Wisk. . \r\n." \r\n"Fred," said the teacher. "You must have known that Wisk''s bad \r\nfor \r\nparrots."\r\n"Oh it wasn''t the Wisk that killed it, sir. It \r\nwas the tumble \r\ndrier."');
INSERT INTO jokes VALUES (3436, 'Dead and dying jokes', 'Doctor, doctor, I''m at death''s door! \r\n\r\nDon''t worry, Mrs Jenkins. An operation will soon pull you \r\n\r\nthrough.');
INSERT INTO jokes VALUES (3437, 'Dead and dying jokes', 'What kind of ghosts haunt operating \r\ntheatres? \r\nSurgical spirits.');
INSERT INTO jokes VALUES (3438, 'Dead and dying jokes', 'How do you make a Venetian \r\nblind?\r\nPoke him in the eye');
INSERT INTO jokes VALUES (3439, 'Dead and dying jokes', 'My brother''s a professional boxer. \r\nHeavyweight ?\r\nNo, featherweight. He tickles his opponents to death !');
INSERT INTO jokes VALUES (3440, 'Dead and dying jokes', 'A monster and a zombie went into a \r\n\r\nfuneral home. ''I''d like to order a coffin for a friend of mine who \r\nhas \r\njust died,'' said the monster. \r\n''Certainly ma''am,'' said the \r\nundertaker, ''but there was really no \r\nneed to bring her with \r\nyou.''');
INSERT INTO jokes VALUES (3441, 'Dead and dying jokes', 'If a woman is born in Italy, grows up \r\nin \r\nEngland, goes to America and dies in Baltimore, what is \r\nshe?\r\nDead.');
INSERT INTO jokes VALUES (3442, 'Dead and dying jokes', 'Vampire 1: "I once went so long without \r\nfresh blood that I \r\nnearly died." \r\nVampire 2: "How awful!" \r\n\r\nVampire 1: "Yes. Fortunately, I found some in the neck of \r\ntime."');
INSERT INTO jokes VALUES (3443, 'Dead and dying jokes', 'I''ve been e-mailing William \r\nShakespeare.\r\nWilliam Shakespeare''s dead, silly.\r\nNo wonder he hasn''t \r\nreplied.');
INSERT INTO jokes VALUES (3444, 'Dead and dying jokes', 'What is the last thing you eat before \r\n\r\nyou die?\r\nYou bite the dust.');
INSERT INTO jokes VALUES (3445, 'Dead and dying jokes', 'What did the little kid do with the \r\ndead \r\nbattery?\r\nHe buried it.');
INSERT INTO jokes VALUES (3446, 'Dead and dying jokes', 'What is posthumous work ?\r\nSomething \r\nwritten by someone after they are dead !');
INSERT INTO jokes VALUES (3447, 'Dead and dying jokes', 'Why did the cowboy \r\ndie with his \r\nboots on ?\r\nBecause he didn''t want to stub his toe when he kicked the \r\nbucket \r\n!');
INSERT INTO jokes VALUES (3448, 'Dead and dying jokes', 'When a knight in armour was killed in \r\nbattle, what sign did they put on \r\nhis grave ?\r\nRust in peace !');
INSERT INTO jokes VALUES (3449, 'Dead and dying jokes', 'Why was George Washington buried at \r\nMount Vernon ?\r\nBecause he was dead !');
INSERT INTO jokes VALUES (3450, 'Dead and dying jokes', 'What are you doing? \r\nI''m trying to \r\ncall Washington! \r\nOh, haven''t you heard? He''s dead!');
INSERT INTO jokes VALUES (3451, 'Dead and dying jokes', 'Waiter, there''s a fly in my soup \r\n\r\n!\r\nYes, it''s the rotting meat that attracts them !');
INSERT INTO jokes VALUES (3452, 'Dead and dying jokes', 'Q: What is the \r\ndefinition of \r\nDeath?\r\nA: When you stop paying taxes suddenly.');
INSERT INTO jokes VALUES (3453, 'Dead and dying jokes', 'Three weeks after her wedding \r\nday, \r\nJoanna called her minister. "Reverend," she wailed, "John and I \r\nhad \r\na DREADFUL fight!" \r\n"Calm down, my child," said the minister, "it''s \r\nnot half as bad as \r\nyou think it is. Every marriage has to have its \r\nfirst fight!" \r\n"I know, I know!" said Joanna, "but what am I going \r\nto do with the \r\nBODY?"');
INSERT INTO jokes VALUES (3454, 'Dead and dying jokes', 'There was a great loss today in the \r\nentertainment world. The \r\nman who wrote the song "Hokey Pokey" died. \r\nWhat was really horrible is \r\nthat they had trouble keeping the body in \r\nthe casket. \r\n\r\nThey put his left leg in....\r\n\r\nWell, you know \r\nthe rest.');
INSERT INTO jokes VALUES (3455, 'Dead and dying jokes', 'A dying man smells his favorite oatmeal \r\n\r\nraisin cookies cooking downstairs. It takes all the strength he has \r\n\r\nleft but he gets up from the bed and crawls down the stairs. He \r\nsees the \r\ncookies cooling on the counter and staggers over to them. As \r\nhe reaches \r\nfor one, his wife''s wrinkled hand reaches out, smacks \r\nhis and she \r\nyells: \r\n"No, you can''t have those! They''re for the \r\nfuneral!"');
INSERT INTO jokes VALUES (3456, 'Dead and dying jokes', '"Do you \r\nbelieve in life after \r\ndeath?" the boss asked one of his employees.\r\n\r\n"Yes, Sir," the new \r\nemployee replied. \r\n\r\n"Well, then, that makes everything just fine," \r\nthe boss went on. \r\n"After you left early yesterday to go to your \r\ngrandmother''s funeral, she \r\nstopped in to see you."');
INSERT INTO jokes VALUES (3457, 'Dead and dying jokes', 'A chap went up to the counter in the \r\nlibrary \r\nand said, "Have you got any books about committing \r\nsuicide?"\r\n\r\nThe librarian said, "Yes. Take a look over there, somewhere on \r\nthe \r\nmiddle shelf."\r\n\r\nThe chap came back a few moments later and \r\nsaid, "I can''t find any at \r\nall."\r\n\r\nThe librarian replied, \r\n"Yes, it''s awful. They never bring ''em \r\nback!"');
INSERT INTO jokes VALUES (3458, 'Dead and dying jokes', 'Teacher: What can you tell me about the \r\nDead Sea?\r\nPupil: Dead ?, I didn''t even know he was sick !');
INSERT INTO jokes VALUES (3459, 'Dead and dying jokes', 'A man is fibbing \r\naway about how \r\ngreat things are in his country. Finally, he starts \r\ndescribing the \r\ntall buildings in his country. \r\n\r\n"There is a building so tall, it \r\ntook my friend Alex 72 hours to fall \r\noff it!" \r\n\r\n"Oh, my God!" \r\nsays his friend. "Surely he must have died!" \r\n\r\n"Of course. He was \r\nwithout food or water for 3 days!"');
INSERT INTO jokes VALUES (3460, 'Dead and dying jokes', 'An English guy was very ill and his son \r\n\r\nwent to visit him in the hospital. Suddenly, the father began to \r\n\r\nbreathe heavily and grabbed the pen and pad by the bed. With his last \r\nounce \r\nof strength he wrote a note, dropped it, and died. \r\n\r\n\r\nThe son was so overcome with grief that he didn''t remember slipping \r\n\r\nthe note into his pocket. At the funeral, he reached into the pocket of \r\n\r\nhis coat and immediately felt the note. He excitedly read it \r\nthinking it \r\nmight be something he could recite during the service. It \r\nsaid: \r\n\r\nYOU WANKER -- GET OFF MY OXYGEN TUBE!!!');
INSERT INTO jokes VALUES (3461, 'Dead and dying jokes', 'This elderly Newfoundland \r\nfisherman \r\nis on his deathbed and summons his 3 sons to his bedside. "Well \r\n\r\nboys, the time is near, and when I pass I''d like to be buried at \r\n\r\nsea." So the boys agreed. A few days after his passing, the local front \r\n\r\npage read, "Local Fishermen Were Shocked Today When Their Nets Brought \r\nin \r\nPatrick McRay in a Coffin, 3 Shovels and the Bodies of His Three \r\n\r\nSons... Funeral arrangements haven''t yet been made, however, it is \r\nbelieved \r\nall wished to be buried at sea."');
INSERT INTO jokes VALUES (3462, 'Dead and dying jokes', 'Phoning the florist to order some \r\n\r\nflowers for her lover''s funeral, woman was caught off guard when asked \r\n\r\nwhat message she wanted on the card. "Message?" she sputtered. \r\n\r\n"Well, I guess, ''You will be missed."'' Visiting the funeral home, she \r\nwas \r\npleased that her floral tribute had arrived but mortified that \r\nthe card \r\nhad her exact words: "I guess you will be missed."');
INSERT INTO jokes VALUES (3463, 'Dead and dying jokes', 'Question: What \r\ndid the dead raccoon \r\nsay in his will? \r\nAnswer: "Leave it to Beaver."');
INSERT INTO jokes VALUES (3464, 'Dead and dying jokes', 'A man was sitting in the electric \r\n\r\nchair. The executioner said, "Look, I''m sorry but I''m going to have to \r\n\r\nthrow the switch in a minute." \r\nThe man said, "Do me a favor and \r\nthrow it out the window!"');
INSERT INTO jokes VALUES (3465, 'Dead and dying jokes', 'Why do cemeteries have fences around \r\n\r\nthem?\r\nBecause people are dying to get in.');
INSERT INTO jokes VALUES (3466, 'Dentist jokes', 'Dentist begging the patient: Could you help \r\nme? \r\nCould you give out a few of your loudest, most painful \r\nscreams?\r\nPatient: Why? Doc, it isn''t all that bad this time.\r\nDentist: \r\nThere are so many people in the waiting room right now and I \r\ndon''t \r\nwant to miss the 4 o''clock ball game.');
INSERT INTO jokes VALUES (3467, 'Dentist jokes', 'Patient: "It must be \r\ntough spending all \r\nday with your hands in someone''s mouth."\r\nDentist: "I just think of it \r\nas having my hands in their \r\nwallet."');
INSERT INTO jokes VALUES (3468, 'Dentist jokes', '"I am sorry, madam, but I shall have to charge \r\nyou hundred dollars for \r\npulling your boy''s tooth."\r\n"Hundred \r\ndollars! Why, I understood you to say that you charged only \r\ntwenty \r\ndollars for such work!"\r\n"Yes," replied the dentist, "but this youngster \r\nyelled so terribly \r\nthat he scared out four other patients out of \r\nthe office."');
INSERT INTO jokes VALUES (3469, 'Dentist jokes', 'What does \r\nthe dentist of the year get?...A \r\nlittle plaque.');
INSERT INTO jokes VALUES (3470, 'Dentist jokes', 'Why did the dentist \r\nmake a poor date with \r\nthe manicurist?\r\nBecause they fought both tooth and nail!');
INSERT INTO jokes VALUES (3471, 'Dentist jokes', 'How many dentists does it take to change a \r\n\r\nlight bulb?\r\nThree. One to administer the anaesthetic, one to \r\nextract the light \r\nbulb, and one to offer the socket some vile pink \r\nmouthwash.');
INSERT INTO jokes VALUES (3472, 'Dentist jokes', 'What did the \r\nwerewolf eat after he''d had \r\nhis teeth taken out? \r\nThe dentist.');
INSERT INTO jokes VALUES (3473, 'Dentist jokes', 'Monster: Doctor, doctor, I''m a blood-sucking \r\nmonster \r\nand I keep needing to eat doctors. \r\nDoctor: Oh what a \r\nshame. I''m a dentist.');
INSERT INTO jokes VALUES (3474, 'Dentist jokes', 'Fred''s mother was on the \r\ntelephone to the \r\nboy''s dentist. "I don''t understand it," she \r\ncomplained, "I \r\nthought his treatment would only cost me $20, but you''ve \r\ncharged me \r\n$80." \r\n"It is usually $20, ma''am," agreed the dentist, "but Fred yelled \r\nso \r\nloudly that three of my other patients ran away!"');
INSERT INTO jokes VALUES (3475, 'Dentist jokes', 'Nigel: You said \r\nthe school dentist would \r\nbe painless, but he wasn''t. \r\nTeacher: Did he hurt you? \r\nNigel: \r\nNo, but he screamed when I bit his finger.');
INSERT INTO jokes VALUES (3476, 'Dentist jokes', 'Why are you laughing? \r\nMy dentist just \r\npulled one of my teeth out. I don''t see much to laugh \r\nabout in that. \r\n\r\nBut it was the wrong one!');
INSERT INTO jokes VALUES (3477, 'Dentist jokes', 'As the judge said to the dentist: Do you \r\n\r\nswear to pull the tooth, the whole tooth, and nothing but the \r\n\r\ntooth?');
INSERT INTO jokes VALUES (3478, 'Dentist jokes', 'Why was the man arrested for looking at sets \r\nof dentures in a dentist''s \r\nwindow? \r\nBecause it was against the \r\nlaw to pick your teeth in public.');
INSERT INTO jokes VALUES (3479, 'Dentist jokes', 'Who has \r\nthe most dangerous job in \r\nTransylvania? \r\nDracula''s dentist.');
INSERT INTO jokes VALUES (3480, 'Dentist jokes', 'I''m suffering from bad breath \r\nYou should \r\ndo something about it!\r\nI did. \r\nI just sent my wife to the \r\ndentist.');
INSERT INTO jokes VALUES (3481, 'Dentist jokes', 'Dentist: Don''t worry. I''m \r\npainless.\r\nPatient: I''m not.');
INSERT INTO jokes VALUES (3482, 'Dentist jokes', 'Patient: Doc, what should I do with all the \r\ngold \r\nand silver in my mouth?\r\nDentist: Don''t smile in a bad \r\nneighborhood.');
INSERT INTO jokes VALUES (3483, 'Dentist jokes', 'Patient: Hey, that tooth \r\nyou pulled \r\nwasn''t the one I wanted pulled.\r\nDentist: Relax, I''m coming to it.');
INSERT INTO jokes VALUES (3484, 'Dentist jokes', 'Father: Don''t you feel better now \r\nthat \r\nyou''ve gone to the dentist?\r\nSon: Sure do. He wasn''t in.');
INSERT INTO jokes VALUES (3485, 'Dentist jokes', 'Cloyd went to a Charleston dentist \r\n\r\ncomplaining his gums had shriveled up and his teeth were falling out. \r\n\r\n\r\nAfter examining him, the dentist said, "Your mouth is really bad. Do \r\n\r\nyou brush?" \r\n\r\n"Ah sure do!" replied Cloyd. "Everee single day!" \r\n\r\n\r\n"What do you brush with?" asked the dentist, \r\n\r\n\r\n"Preparation H," said the redneck.');
INSERT INTO jokes VALUES (3486, 'Dentist jokes', 'I thought, Miss Smith, that you wanted \r\n\r\nyesterday afternoon off because you were seeing your dentist?\r\nThat''s \r\nright, Sir.\r\nSo how come I saw you coming out of the movie theatre \r\nwith a friend? \r\nThat was my dentist.');
INSERT INTO jokes VALUES (3487, 'Dentist jokes', 'At what time do most people go to the \r\ndentist?\r\nAt tooth-hurty (2:30).');
INSERT INTO jokes VALUES (3488, 'Dentist jokes', 'What did the dentist say to the \r\ngolfer?\r\n"You have a hole in one. "');
INSERT INTO jokes VALUES (3489, 'Dentist jokes', 'Why does a dentist seem moody?\r\nBecause he \r\nalways looks down in the mouth.');
INSERT INTO jokes VALUES (3490, 'Dentist jokes', 'Why do dentists like \r\npotatoes?\r\nBecause \r\nthey are so filling.');
INSERT INTO jokes VALUES (3491, 'Dentist jokes', 'What is a drill team?\r\nA group of dentists \r\nwho work together.');
INSERT INTO jokes VALUES (3492, 'Dentist jokes', 'Why do people dislike going to \r\nthe \r\ndentist?\r\nBecause he is boring.');
INSERT INTO jokes VALUES (3493, 'Dentist jokes', 'What is a dentist''s office?\r\nA filling \r\nstation.');
INSERT INTO jokes VALUES (3494, 'Dentist jokes', 'What do you call the Scottish dentist \r\n?\r\nPhil McCavity !');
INSERT INTO jokes VALUES (3495, 'Dentist jokes', 'What do you call a dentist in the army ?\r\nA \r\ndrill sergeant !');
INSERT INTO jokes VALUES (3496, 'Dentist jokes', 'What time is it when you have to go to the \r\n\r\ndentist ?\r\nTooth Hurty !');
INSERT INTO jokes VALUES (3497, 'Dentist jokes', 'Did you hear about the dentist who planted a \r\ngarden?...\r\nA month later he was picking his teeth');
INSERT INTO jokes VALUES (3498, 'Dentist jokes', 'What game did the dentist play \r\nwhen she \r\nwas a child?...Caps and robbers');
INSERT INTO jokes VALUES (3499, 'Dentist jokes', 'What does a dentist do on a \r\nroller \r\ncoaster?...He braces himself');
INSERT INTO jokes VALUES (3500, 'Dentist jokes', 'What did the dentist see at the \r\nNorth \r\nPole?...A molar bear');
INSERT INTO jokes VALUES (3501, 'Dentist jokes', 'What was the dentist doing in \r\nPanama?...Looking \r\nfor the Root Canal');
INSERT INTO jokes VALUES (3502, 'Dentist jokes', 'Where does the dentist get his gas?...At the \r\n\r\nfilling station');
INSERT INTO jokes VALUES (3503, 'Dentist jokes', 'Why didn''t the dentist ask his secretary \r\nout?...He was \r\nalready taking out a tooth');
INSERT INTO jokes VALUES (3504, 'Dentist jokes', 'What did the dentist say to the \r\n\r\ncomputer?...This won''t hurt a byte');
INSERT INTO jokes VALUES (3505, 'Dentist jokes', 'What did the tooth say to the departing \r\n\r\ndentist?...Fill me in when you get back');
INSERT INTO jokes VALUES (3506, 'Dentist jokes', 'Anyone know the six most frightening words in \r\n\r\nthe world ??? "The Dentist will see you now."');
INSERT INTO jokes VALUES (3507, 'Dentist jokes', '"Open wider." \r\nrequested the dentist, as he \r\nbegan his examination of the patient. "Good God \r\n!" he said \r\nstartled. "You''ve got the biggest cavity I''ve ever seen \r\n- the biggest \r\ncavity I''ve ever seen." "OK Doc !" replied the \r\npatient. "I''m scared \r\nenough without you saying something like that twice." \r\n"I didn''t !" \r\nsaid the dentist. "That was the echo."');
INSERT INTO jokes VALUES (3508, 'Dentist jokes', 'While I was \r\nwaiting to see the dentist, a \r\nwoman came out of his inner office \r\nsmiling. Nodding to me, she \r\nsaid, "Thank goodness my work is completed. I''m \r\nso glad to have found \r\na painless dentist and one who''s so gentle and \r\nunderstanding too." \r\nWhen seated in the dentist chair, I related the \r\nincident to the \r\ndoctor. He laughed and explained, "Oh, that was just my \r\nMother."');
INSERT INTO jokes VALUES (3509, 'Dentist jokes', '"I came in to make an appointment with the \r\ndentist." said \r\nthe man to the receptionist." "I''m sorry sir." she \r\nreplied. "He''s \r\nout right now, but..." "Thank you." interrupted the \r\nobviously \r\nnervous prospective patient. "When will he be out again \r\n?"');
INSERT INTO jokes VALUES (3510, 'Dentist jokes', 'Patient: \r\nDoctor, I am very nervous. You \r\nknow, this is my first extraction.\r\nYoung dentist: Don''t worry, it''s \r\nmy first extraction too.');
INSERT INTO jokes VALUES (3511, 'Dentist jokes', 'Dentist: There goes the only woman I ever \r\n\r\nloved.\r\nAssistant: Why don''t you marry her?\r\nDentist: I can''t \r\nafford to. She''s my best patient.');
INSERT INTO jokes VALUES (3512, 'Dentist jokes', 'Dentist: Just \r\nlet me finish and you will \r\nbe another man after these cosmetic \r\nprocedures.\r\nPatient: Okay \r\ndoc, but don''t forget to send your bill to the other \r\nman.');
INSERT INTO jokes VALUES (3513, 'Dentist jokes', 'Dentist to parsimonious patient "No, we give \r\nno discount for empty \r\nspaces when cleaning and polishing teeth Mrs. \r\nBorde!"');
INSERT INTO jokes VALUES (3514, 'Dentist jokes', 'Young Charlie \r\nto dentist''s sexy chariside \r\nassistant "Aha ! Are you the lady \r\northodontist ?".\r\nThe lady \r\nreplied "No, but I''ll straighten anyone''s teeth "');
INSERT INTO jokes VALUES (3515, 'Dentist jokes', 'Young \r\nlady to father "Daddy, when I grow \r\nup shall I become a heart-doctor or \r\na tooth-doctor " \r\n"Dentist" \r\n"Why father ?" "We have only one heart, but 32 \r\nteeth!"');
INSERT INTO jokes VALUES (3516, 'Dentist jokes', 'Patient to Dentist: "How much to get my teeth \r\n\r\nstraightened?" \r\n"Twenty thousand bucks" Patient heads for the \r\ndoor.\r\nDentist to patient: "Where are you going?"\r\n"To a plastic \r\nsurgeon to get my mouth bent."');
INSERT INTO jokes VALUES (3517, 'Dentist jokes', 'Patient: How much to \r\nhave this tooth \r\npulled? \r\nDentist: With pain $200 and without pain $100. \r\n\r\nPatient: \r\nWell, without pain it''s cheaper. Pull it WITHOUT pain.\r\n\r\nWithout \r\nanesthesia neither anything, the dentist begins to extract the \r\n\r\ntooth, when the patient outcry: Aaaahhhhhhhh !!!!!\r\n\r\nHey, WITH pain it \r\ncosts $200 !!!, replies the dentist.');
INSERT INTO jokes VALUES (3518, 'Dentist jokes', 'What''s worse \r\nthan having your doctor tell \r\nyou that you have VD? \r\nHaving your dentist tell you.');
INSERT INTO jokes VALUES (3519, 'Dentist jokes', 'A patient asked the dentist, if it \r\nwasn''t \r\nnasty to be all the day with the hands in someone''s mouth.\r\nThe \r\ndentist answered "I just think of it as having my hands in their \r\n\r\nwallet."');
INSERT INTO jokes VALUES (3520, 'Dentist jokes', 'Gerald: "Have you ever come across a man who, \r\nat the \r\nslightest touch, caused you to thrill and tremble in every \r\nfiber of your \r\nbeing?"\r\nMabel: "Yes, the dentist."');
INSERT INTO jokes VALUES (3521, 'Dentist jokes', 'believe that the members of the dental \r\n\r\nprofession are the only men who can tell a women to open or close her \r\nmouth and \r\nget away with it.');
INSERT INTO jokes VALUES (3522, 'Dentist jokes', 'Patient:Do you extract teeth \r\npainlessly?\r\nDentis: "Not always, the other day I nearly dislocated my \r\nwrist');
INSERT INTO jokes VALUES (3523, 'Dentist jokes', 'Papa, why is it that dentists call their \r\noffices dental parlors?"\r\n"Because they are drawing-rooms, my son."');
INSERT INTO jokes VALUES (3524, 'Dentist jokes', 'Pardon me for a moment, \r\nplease," said the \r\ndentist to the victim, "but before beginning this \r\nwork I must have \r\nmy drill."\r\n"Good heavens, man!" exclaimed the patient irritably. \r\n"Can''t you \r\npull a tooth without a rehearsal?"');
INSERT INTO jokes VALUES (3525, 'Dentist jokes', 'What to do you call an old dentist? \r\nA bit \r\nlong in the tooth');
INSERT INTO jokes VALUES (3526, 'Dentist jokes', 'A man went to his dentist because he feels \r\n\r\nsomething wrong in his mouth.\r\nThe dentist examines him and says, \r\n"that new upper plate I put in for \r\nyou six months ago is eroding. \r\nWhat have you been eating?"\r\nThe man replies, "all I can think of is \r\nthat about four months ago my \r\nwife made some asparagus and put some \r\nstuff on it that was delicious \r\n... Hollandaise sauce. I loved it so \r\nmuch I now put it on everything --- \r\nmeat, toast, fish, vegetables, \r\neverything."\r\n"Well," says the dentist, "that''s probably the problem. \r\nHollandaise \r\nsauce is made with lots of lemon juice, which is highly \r\ncorrosive. \r\nIt''s eaten away your upper plate. I''ll make you a new \r\nplate, and this \r\ntime use chrome."\r\n"Why chrome?" asks the \r\npatient. To which the dentist replies, "It''s \r\nsimple. Everyone knows that \r\n... there''s no plate like chrome for the \r\nHollandaise!"');
INSERT INTO jokes VALUES (3527, 'Dentist jokes', 'Dentist: "You don''t need to open your mouth \r\nany \r\nwider. When I pull your tooth I expect to stand outside."');
INSERT INTO jokes VALUES (3528, 'Dentist jokes', '"Did you get \r\nyour money?" ask the wife of \r\nthe dentist who had just return from the \r\ndelinquent patient''s \r\nhome.\r\n"Not a cent," growled the dentist, "and worse than that, he \r\ninsulted \r\nme, and gnashed my teeth at me!"');
INSERT INTO jokes VALUES (3529, 'Dinosaur jokes', 'Q: Why did the dinosaur cross the road?\r\nA: \r\nBecause the chicken wasn''t invented yet.');
INSERT INTO jokes VALUES (3530, 'Dinosaur jokes', 'Q1: What vehicle does \r\nT-Rex use to go \r\nfrom planet to planet?\r\nA: A Dinosaucer');
INSERT INTO jokes VALUES (3531, 'Dinosaur jokes', 'Q2: What kind of materials do dinosaurs use \r\nfor the \r\nfloor of their homes?\r\nA: Rep Tiles');
INSERT INTO jokes VALUES (3532, 'Dinosaur jokes', 'Q3: What did the female dinosaur call her \r\nblouse making \r\nbusiness?\r\nA: Try Sara''s Tops');
INSERT INTO jokes VALUES (3533, 'Dinosaur jokes', 'Q4: What is the head of an Italian dinosaur \r\n\r\nfamily called?\r\nA: Ptera Don');
INSERT INTO jokes VALUES (3534, 'Dinosaur jokes', 'Q5: How do you ask a dinosaur to lunch?\r\nA: \r\nTea Rex?');
INSERT INTO jokes VALUES (3535, 'Dinosaur jokes', 'Q: What do you say to a twenty ton dinosaur \r\nwith \r\nheadphones on?\r\nA: Anything you want. He can''t hear you.');
INSERT INTO jokes VALUES (3536, 'Dinosaur jokes', 'Q: Why didn''t the T-rex \r\nskeleton attack \r\nthe museum visitors?\r\nA: Because she had no guts!');
INSERT INTO jokes VALUES (3537, 'Dinosaur jokes', 'Q: What do you get when you put a bomb \r\n\r\nand a dinosaur together?\r\nA: Dino-mite.');
INSERT INTO jokes VALUES (3538, 'Dinosaur jokes', 'Q: What do you call a Blind Dinosaur?\r\nA: \r\nDo-ya-think-he-saurus.');
INSERT INTO jokes VALUES (3539, 'Dinosaur jokes', 'Q: What do you call a Blind Dinosaur''s \r\nDog?\r\nA: Do-ya-think-he-saurus-rex.');
INSERT INTO jokes VALUES (3540, 'Dinosaur jokes', 'Q: What was the most flexiest dinosaur?\r\nA: \r\nTyrannosaurus Flex.');
INSERT INTO jokes VALUES (3541, 'Dinosaur jokes', 'Q: Why did the Apatosaurus devour the \r\nfactory? \r\nA: Because she was a plant eater!');
INSERT INTO jokes VALUES (3542, 'Dinosaur jokes', 'Q: Why did the T-Rex eat \r\n\r\nhamburgers?\r\nA: Because he is a meat eater!');
INSERT INTO jokes VALUES (3543, 'Dinosaur jokes', 'Why does a dinosaur have cracks \r\nbetween \r\nhis toes?\r\nTo carry his library card.');
INSERT INTO jokes VALUES (3544, 'Dinosaur jokes', 'Why don''t more dinosaurs join the police \r\n\r\nforce?\r\nThey can''t hide behind billboards.');
INSERT INTO jokes VALUES (3545, 'Dinosaur jokes', 'Why did the dinosaur walk on two \r\n\r\nlegs?\r\nTo give the ants a chance.');
INSERT INTO jokes VALUES (3546, 'Dinosaur jokes', 'Why is it dangerous to go into the jungle \r\n\r\nbetween two and four in the afternoon?\r\nThat''s when dinosaurs are \r\njumping out of palm trees.');
INSERT INTO jokes VALUES (3547, 'Dinosaur jokes', 'Why does a \r\ndinosaur climb a tree?\r\nTo \r\nget in his nest.');
INSERT INTO jokes VALUES (3548, 'Dinosaur jokes', 'Why don''t dinosaurs take ballet \r\nlessons?\r\nThey outgrew their leotards.');
INSERT INTO jokes VALUES (3549, 'Dinosaur jokes', 'Why do dinosaurs climb trees?\r\nThere''s \r\nnothing else to climb in the jungle.');
INSERT INTO jokes VALUES (3550, 'Dinosaur jokes', 'How can you tell a male \r\ndinosaur from a \r\nfemale dinosaur?\r\nAsk it a question. If he answers, it''s a male; if \r\nshe answers, it''s \r\nfemale.');
INSERT INTO jokes VALUES (3551, 'Dinosaur jokes', 'How can you tell if a dinosaur is visiting \r\nyour house?\r\nHis tricycle will be parked outside.');
INSERT INTO jokes VALUES (3552, 'Dinosaur jokes', 'Why do dinosaurs wear glasses?\r\nTo make \r\nsure they don''t step on other dinosaurs.');
INSERT INTO jokes VALUES (3553, 'Dinosaur jokes', 'What''s red on the \r\noutside and green on \r\nthe inside?\r\nA dinosaur wearing red pajamas.');
INSERT INTO jokes VALUES (3554, 'Dinosaur jokes', 'What are prehistoric monsters called when \r\nthey \r\nsleep ?\r\nA dinosnore !');
INSERT INTO jokes VALUES (3555, 'Dinosaur jokes', 'What followed the dinosaur ?\r\nIt''s tail \r\n!');
INSERT INTO jokes VALUES (3556, 'Dinosaur jokes', 'What did you call a dinosaur that keeps you \r\nawake at \r\nnight ?\r\nBronto-snore-us !');
INSERT INTO jokes VALUES (3557, 'Dinosaur jokes', 'Q: What dinosaur would you find in a rodeo? \r\n\r\nA: Bronco-saurus!');
