INSERT INTO jokes VALUES (3932, 'Doctor and nurse jokes', 'Doctor, Doctor Have you got something \r\n\r\nfor a bad headache?\r\nOf course. Just take this hammer and hit \r\nyourself in the head. Then \r\nyou''ll have a bad headache.');
