INSERT INTO jokes VALUES (3937, 'Doctor and nurse jokes', 'Doctor, Doctor I''m on a diet and \r\n\r\nit''s making me irritable. Yesterday I bit someones ear off.\r\nOh \r\ndear, that''s a lot of calories!');
INSERT INTO jokes VALUES (3938, 'Doctor and nurse jokes', 'Doctor, Doctor Can I have second \r\n\r\nopinion?\r\nOf course, come back tomorrow!');
INSERT INTO jokes VALUES (3939, 'Doctor and nurse jokes', 'Doctor, Doctor you have to help me \r\n\r\nout!\r\nCertainly, which way did you come in?');
