INSERT INTO jokes VALUES (3941, 'Doctor and nurse jokes', 'Doctor, Doctor I keep \r\nthinking \r\nI''m invisible\r\nWho said that?');
INSERT INTO jokes VALUES (3942, 'Doctor and nurse jokes', 'Doctor, Doctor I keep thinking I''m a \r\n\r\nsnake about to shed it''s skin.\r\nWhy don''t you go behind the \r\nscreen and slip into something more \r\ncomfortable then!');
INSERT INTO jokes VALUES (3943, 'Doctor and nurse jokes', 'Doctor: You need new glasses \r\n\r\nPatient: How do you know?, I haven''t told you whats wrong with me \r\nyet\r\nDoctor: I could tell as soon as you walked in through the \r\nwindow!');
