INSERT INTO jokes VALUES (3947, 'Doctor and nurse jokes', 'Doctor, Doctor I''m boiling \r\nup!\r\nJust simmer down!');
INSERT INTO jokes VALUES (3948, 'Doctor and nurse jokes', 'Doctor, Doctor I think I''m an adder \r\n\r\nGreat, can you help me with my accounts then please!');
INSERT INTO jokes VALUES (3949, 'Doctor and nurse jokes', 'Doctor, Doctor I \r\nkeep painting \r\nmyself gold \r\nDon''t worry it''s just a gilt complex!');
