INSERT INTO jokes VALUES (3955, 'Doctor and nurse jokes', 'Doctor, Doctor When I press with my \r\nfinger \r\nhere... it hurts, and here... it hurts, and here... and here... \r\nWhat \r\ndo you think is wrong with me?\r\nYou have a broken finger!');
INSERT INTO jokes VALUES (3956, 'Doctor and nurse jokes', 'Doctor, Doctor When I press with my \r\nfinger \r\nhere... it hurts, and here... it hurts, and here... and \r\nhere... What do \r\nyou think is wrong with me?\r\nYou have a broken \r\nfinger!');
INSERT INTO jokes VALUES (3957, 'Doctor and nurse jokes', 'Doctor Doctor I feel like \r\nbiscuits!\r\nWhat, you mean those square ones?\r\nYes!\r\nThe ones you put butter \r\non?\r\nYes!\r\nOh, You''re Crackers!');
INSERT INTO jokes VALUES (3958, 'Doctor and nurse jokes', 'Doctor, Doctor My little boy has just \r\nswallowed \r\na roll of film!\r\nHmmmm. Let''s hope nothing \r\ndevelops.');
INSERT INTO jokes VALUES (3959, 'Doctor and nurse jokes', 'Doctor, Doctor I feel like a \r\npair \r\nof curtains\r\nWell pull yourself together then');
INSERT INTO jokes VALUES (3960, 'Doctor and nurse jokes', 'Doctor, Doctor I snore so loud I \r\n\r\nkeep myself awake\r\nSleep in another room then!');
