INSERT INTO jokes VALUES (3971, 'Doctor and nurse jokes', 'Doctor, Doctor, some days I feel like \r\na tee-pee and \r\nother days I feel like a wig-wam. \r\nYou''re too \r\ntents.');
INSERT INTO jokes VALUES (3972, 'Doctor and nurse jokes', 'Doctor, Doctor I keep thinking I''m a \r\n\r\ndog.\r\nSit on the couch and we will talk about it.\r\nBut I''m not \r\nallowed up on the couch!');
INSERT INTO jokes VALUES (3973, 'Doctor and nurse jokes', 'Doctor, Doctor I''ve a split \r\n\r\npersonality \r\nWell, you''d better both sit down then!');
INSERT INTO jokes VALUES (3974, 'Doctor and nurse jokes', 'Doctor, Doctor I keep \r\nthinking \r\nI''m a nit \r\nWill you get out of my hair!');
INSERT INTO jokes VALUES (3975, 'Doctor and nurse jokes', 'Doctor Doctor I keep thinking I''m a \r\n\r\ncaterpillar \r\nDon''t worry you''ll soon change!');
INSERT INTO jokes VALUES (3976, 'Doctor and nurse jokes', 'Doctor, Doctor you''ve taken out my \r\n\r\ntonsils, my adenoids, my gall bladder, my varicose veins and my \r\n\r\nappendix, but I still don''t feel well.\r\nThat''s quite enough out of \r\nyou!');
INSERT INTO jokes VALUES (3977, 'Doctor and nurse jokes', 'Doctor, Doctor I''ve got bad teeth, \r\n\r\nfoul breath and smelly feet.\r\nSounds like you''ve got Foot and \r\nMouth disease!');
INSERT INTO jokes VALUES (3978, 'Doctor and nurse jokes', 'Doctor, Doctor my \r\nhusband smells \r\nlike fish\r\nPoor sole!');
