INSERT INTO jokes VALUES (3990, 'Doctor and nurse jokes', 'Doctor, Doctor I feel like a \r\nneedle.\r\nI see your point! \r\nTell me straight Doc, Is it bad?\r\nWell, I \r\nouldn''t start watching any new soap operas!');
INSERT INTO jokes VALUES (3991, 'Doctor and nurse jokes', 'Doctor, Doctor \r\nI''m having \r\ntrouble with my breathing.\r\nI''ll give you something that will soon put a \r\nstop to that!');
INSERT INTO jokes VALUES (3992, 'Doctor and nurse jokes', 'Doctor, Doctor what did the x-ray of \r\n\r\nmy head show?\r\nAbsolutely nothing!');
INSERT INTO jokes VALUES (3993, 'Doctor and nurse jokes', 'Doctor Doctor I''m so ugly what can I \r\ndo about \r\nit?\r\nHire yourself out for Halloween parties!');
