INSERT INTO jokes VALUES (3995, 'Doctor and nurse jokes', 'Doctor, Doctor \r\nYou''ve got to \r\nhelp me - I just can''t stop my hands shaking\r\nDo you drink a \r\nlot?\r\nNot really - I spill most of it!');
INSERT INTO jokes VALUES (3996, 'Doctor and nurse jokes', 'Doctor, Doctor I keep thinking I''m \r\n\r\na woodworm \r\nHow boring for you!');
