INSERT INTO jokes VALUES (4199, 'Dog jokes', 'What should you do if you see a vicious \r\ndog?\r\nHope he doesn''t see you.');
INSERT INTO jokes VALUES (4200, 'Dog jokes', 'What should you know before you teach your \r\ndog \r\na new trick?\r\nYou should know more than your dog.');
INSERT INTO jokes VALUES (4201, 'Dog jokes', 'What was the dog doing on the turnpike?\r\nAbout \r\nseven miles an hour.');
