INSERT INTO jokes VALUES (4216, 'Dog jokes', 'Why did the dog sleep so poorly?\r\nBy mistake he \r\nplugged his electric blanket into the toaster and kept \r\npopping out \r\nof bed all night!');
INSERT INTO jokes VALUES (4217, 'Dog jokes', 'Why did the dog''s owner think his dog \r\nwas a \r\ngreat mathematician?\r\nWhen he asked the dog what six minus six was, \r\nthe dog said \r\nnothing.');
INSERT INTO jokes VALUES (4218, 'Dog jokes', 'Why did the thoughtful father buy his six children \r\na dachshund?\r\nHe wanted a dog they could all pet at once.');
