INSERT INTO jokes VALUES (4374, 'Easter jokes', 'Would \r\nyou like something from my Easter \r\nbasket?\r\n"Sure!"\r\n"Here. Have some plastic grass."');
INSERT INTO jokes VALUES (4375, 'Easter jokes', '"Why are you studying your Easter \r\n\r\ncandy?"\r\n"I''m trying to decide which came first-the chocolate chicken or the \r\n\r\nchocolate egg!"');
INSERT INTO jokes VALUES (4376, 'Easter jokes', 'Did you hear the one about the Easter Bunny who \r\n\r\nsat on a bee?\r\nIt''s a tender tail!');
INSERT INTO jokes VALUES (4377, 'Easter jokes', 'What happened when the Easter Bunny caught his \r\n\r\nhead in the fan?\r\nIt took ears off his life!');
INSERT INTO jokes VALUES (4378, 'Easter jokes', 'What would you get if you crossed the \r\n\r\nEaster Bunny with a leprechaun?\r\nThe Easter Blarney!');
INSERT INTO jokes VALUES (4379, 'Easter jokes', 'Why did you drive the lawn mower over your \r\nEaster \r\nbasket?\r\nI thought the plastic grass was growing too high!');
INSERT INTO jokes VALUES (4380, 'Easter jokes', 'Why do we paint \r\nEaster eggs?\r\nBecause \r\nit''s easier than trying to wallpaper them!');
