INSERT INTO jokes VALUES (4384, 'Easter jokes', 'What''s red and blue and \r\nsogs up your \r\nEaster basket? \r\nColoured scrambled eggs');
INSERT INTO jokes VALUES (4385, 'Easter jokes', 'What''s big and purple and hugs your Easter \r\n\r\nbasket? \r\nThe Easter Barney!');
INSERT INTO jokes VALUES (4386, 'Easter jokes', 'How does the Easter Bunny paint all of those \r\n\r\neggs? \r\nHe hires Santa''s elves during the off-season.');
