INSERT INTO jokes VALUES (4388, 'Easter jokes', 'Who delivers Easter treats to all the \r\nfish \r\nin the sea? \r\nThe Oyster Bunny!');
INSERT INTO jokes VALUES (4389, 'Easter jokes', 'What will the Easter Bunny be doing after \r\nEaster? \r\nOne to three for breaking and entering.');
INSERT INTO jokes VALUES (4390, 'Easter jokes', 'How do you catch the Easter \r\nBunny? \r\nHide \r\nin the bushes and make a noise like a carrot!');
INSERT INTO jokes VALUES (4391, 'Easter jokes', 'How should you send a letter to the Easter \r\n\r\nBunny? \r\nBy hare mail!');
