INSERT INTO jokes VALUES (4393, 'Easter jokes', 'What is a dog''s favourite Easter \r\ntreat? \r\n\r\nJelly bones!');
INSERT INTO jokes VALUES (4394, 'Easter jokes', 'What has big ears, brings Easter treats, and \r\ngoes \r\n"hippity-BOOM, hippity-BOOM, hippity-BOOM"? \r\nThe Easter \r\nElephant.');
INSERT INTO jokes VALUES (4395, 'Easter jokes', 'How does the Easter Bunny stay in shape? \r\nHe \r\ndoes lots of bare-obics.');
INSERT INTO jokes VALUES (4396, 'Easter jokes', 'A man wanted an Easter pet for his daughter. He \r\n\r\nlooked at a baby chick and a baby duck. They were both very cute, \r\nbut \r\nhe \r\ndecided to buy the baby chick. Do you know why? \r\nThe \r\nbaby chick was a little cheeper!');
INSERT INTO jokes VALUES (4397, 'Easter jokes', 'Why did the Easter Bunny have to \r\nfire the \r\nduck? \r\nBecause he kept quacking all the eggs!');
INSERT INTO jokes VALUES (4398, 'Easter jokes', 'What''s the difference between \r\nthe Easter \r\nBunny and a silly monster ?\r\nOne''s a hare-head and the other''s an \r\nair-head!');
INSERT INTO jokes VALUES (4399, 'Easter jokes', 'What would you get \r\nif you crossed the \r\nEaster Bunny with Chinese food?\r\nHop suey!');
INSERT INTO jokes VALUES (4400, 'Easter jokes', 'What has long ears, hops and likes \r\nwebsurfing?\r\nThe e-aster bunny.');
INSERT INTO jokes VALUES (4401, 'Easter jokes', 'What is the best way to send a letter to the \r\n\r\nEaster Bunny?\r\nBy hare (air) mail.');
