INSERT INTO jokes VALUES (4433, 'Elephant jokes', 'What''s the difference between an elephant \r\nand \r\na piece of paper ?\r\nYou can''t make a paper aeroplane out of an \r\nelephant !');
INSERT INTO jokes VALUES (4434, 'Elephant jokes', 'What''s the \r\ndifference between a sick \r\nelephant and seven days ?\r\nOne is a weak one and the other one week !');
INSERT INTO jokes VALUES (4435, 'Elephant jokes', 'What''s the difference \r\nbetween an \r\nelephant and a banana ?\r\nHave you ever tried to peel an elephant ?');
INSERT INTO jokes VALUES (4436, 'Elephant jokes', 'Why are elephants grey ?\r\nSo you can tell \r\nthem from flamingos !');
INSERT INTO jokes VALUES (4437, 'Elephant jokes', 'What''s the difference between \r\nan African \r\nelephant and an Indian elephant ?\r\nAbout 3,000 miles !');
INSERT INTO jokes VALUES (4438, 'Elephant jokes', 'What''s the difference between an elephant \r\nand a \r\ngooseberry ?\r\nA gooseberry is green !');
INSERT INTO jokes VALUES (4439, 'Elephant jokes', 'What pill would you give to an elephant that \r\n\r\ncan''t sleep ?\r\nTrunkquilizers !');
INSERT INTO jokes VALUES (4440, 'Elephant jokes', 'A boy with an elephant on his head went to \r\nsee a \r\ndoctor. The doctor said, "You know you really need \r\nhelp"\r\n"Yes I do", said the elephant, "get this kid off my foot !"');
INSERT INTO jokes VALUES (4441, 'Elephant jokes', 'What kind of elephants live in Antartica \r\n?\r\nCold ones !');
INSERT INTO jokes VALUES (4442, 'Elephant jokes', 'Why do elephants have trunks ?\r\nBecause \r\nthey would look silly carrying suitcases !');
INSERT INTO jokes VALUES (4443, 'Elephant jokes', 'How do you fit \r\nfive elephants into a car \r\n?\r\nTwo in the front, two in the back and the other in the glove \r\n\r\ncompartment !');
INSERT INTO jokes VALUES (4444, 'Elephant jokes', 'How does an elephant get out of a small car \r\n?\r\nThe same way that he got in !');
INSERT INTO jokes VALUES (4445, 'Elephant jokes', 'Why did the elephant cross the road \r\n?\r\nBecause the chicken was having a day off !');
INSERT INTO jokes VALUES (4446, 'Elephant jokes', 'What did the hotel manager say to the \r\nelephant \r\nthat couldn''t pay his bill ? "Pack your trunk and clear out \r\n!"');
INSERT INTO jokes VALUES (4447, 'Elephant jokes', 'How \r\ndo you get an elephant into a \r\nmatchbox ?\r\nTake all the matches out first !');
INSERT INTO jokes VALUES (4448, 'Elephant jokes', 'What did the grape say when the \r\nelephant \r\nstood on it ?\r\nNothing, it just let out a little wine !');
INSERT INTO jokes VALUES (4449, 'Elephant jokes', 'How can you tell if there is \r\nan elephant \r\nin your dessert ?\r\nYou get very lumpy ice cream !');
INSERT INTO jokes VALUES (4450, 'Elephant jokes', 'Why are elephants wiser than chickens \r\n\r\n?\r\nHave you ever heard of Kentucky Fried Elephant ?!');
INSERT INTO jokes VALUES (4451, 'Elephant jokes', 'Why do elephants eat raw food ?\r\nBecause \r\nthey don''t know how to cook !');
INSERT INTO jokes VALUES (4452, 'Elephant jokes', 'Have you heard about the \r\nelephant that \r\nwent on a crash diet ?\r\nHe wrecked three cars, a bus and two fire \r\nengines !');
