INSERT INTO jokes VALUES (4454, 'Elephant jokes', 'When should you feed milk to a baby elephant \r\n\r\n?\r\nWhen it''s a baby elephant !');
INSERT INTO jokes VALUES (4455, 'Elephant jokes', 'How do you know that peanuts are \r\n\r\nfattening ?\r\nHave you ever seen a skinny elephant ?');
INSERT INTO jokes VALUES (4456, 'Elephant jokes', 'What do you call an elephant that can''t do \r\n\r\nsums ?\r\nDumbo !');
INSERT INTO jokes VALUES (4457, 'Elephant jokes', 'Bill:"My homework is really difficult \r\ntonight, I''ve to \r\nwrite an essay on an elephant."?\r\nBert:"Well, for a \r\nstart your going to need a big ladder.."');
INSERT INTO jokes VALUES (4458, 'Elephant jokes', 'Why do \r\nelephants do well in school \r\n?\r\nBecause they have a lot of grey matter !');
INSERT INTO jokes VALUES (4459, 'Elephant jokes', 'Teacher:"To which family \r\ndoes the \r\nelephant belong ?"\r\nPupil:"I don''t know, nobody I know owns one !"');
INSERT INTO jokes VALUES (4460, 'Elephant jokes', 'Teacher: "Name six \r\nwild \r\nanimals"\r\nPupil:"Four elephants and two lions !"');
INSERT INTO jokes VALUES (4461, 'Elephant jokes', 'Teacher: "Where would you find an elephant \r\n\r\n?"\r\nPupil:"You don''t have to find them, they''re too big to lose \r\n\r\n!"');
INSERT INTO jokes VALUES (4462, 'Elephant jokes', 'Tarzan was tired when he came home.\r\n"What \r\nhave you been doing", asked Jane.\r\n"Chasing a herd of elephants on \r\nvines"\r\n"Really ?", said Jane. "I thought elephants stayed on the \r\nground \r\n!"');
INSERT INTO jokes VALUES (4463, 'Elephant jokes', 'What is the easy way to get a wild elephant \r\n?\r\nGet a tame one and annoy it !');
INSERT INTO jokes VALUES (4464, 'Elephant jokes', 'What did Tarzan say when he saw the \r\n\r\nelephants coming ?\r\nHere come the elephants');
INSERT INTO jokes VALUES (4465, 'Elephant jokes', 'What''s the best way to see a charging herd \r\n\r\nof elephants ?\r\nOn television !');
INSERT INTO jokes VALUES (4466, 'Elephant jokes', 'Why don''t elephants like playing cards in \r\nthe \r\njungle ?\r\nBecause of all the cheetahs !');
INSERT INTO jokes VALUES (4467, 'Elephant jokes', 'What do you call an elephant with a \r\n\r\ncarrot in each ear ?\r\nAnything you want as he can''t hear you !');
INSERT INTO jokes VALUES (4468, 'Elephant jokes', 'What do you call an \r\nelephant that lies \r\nacross the middle of a tennis court ?\r\nAnnette !');
