INSERT INTO jokes VALUES (4472, 'Elephant jokes', 'How to elephants talk to each other ?\r\nBy \r\n''elephone !');
INSERT INTO jokes VALUES (4473, 'Elephant jokes', 'What do you give an elephant with big feet \r\n?\r\nPlenty of room !');
INSERT INTO jokes VALUES (4474, 'Elephant jokes', 'Why do elephants have trunks ?\r\nBecause \r\nthey''ve no pockets to put things in !');
