INSERT INTO jokes VALUES (4476, 'Elephant jokes', 'What do you find in an elephants graveyard \r\n?\r\nElephantoms !');
INSERT INTO jokes VALUES (4477, 'Elephant jokes', 'What is stronger an elephant or a snail \r\n?\r\nA snail, because it carries it''s house, an elephant just carries its \r\n\r\ntrunk !');
INSERT INTO jokes VALUES (4478, 'Elephant jokes', 'What do you do with old cannon balls \r\n?\r\nGive them to elephants to use as marbles !');
