INSERT INTO jokes VALUES (5257, 'Gorilla jokes', 'When the \r\nlumberjacks sawed down the tree, \r\nwhere did the Gorilla hiding in the uppermost \r\nbranches \r\nland?\r\nNearby - the Ape-lle doesn''t fall far from the tree!');
