INSERT INTO jokes VALUES (5274, 'Gorilla jokes', 'Why did the \r\nactor fire his Gorilla \r\nagent?\r\nThe big Ape kept wanting to take more than a 10% bite!');
INSERT INTO jokes VALUES (5275, 'Gorilla jokes', 'Why did the Ape jump off the building?\r\nHe \r\nwanted to show the world the stuff he was made of!');
INSERT INTO jokes VALUES (5276, 'Gorilla jokes', 'Why did the \r\ngirl Gorilla, engaged to the \r\ninvisible man, call off the wedding?\r\nBecause in the last analysis she \r\njust couldn''t see it!');
INSERT INTO jokes VALUES (5277, 'Gorilla jokes', 'Why did the \r\nGorilla enlist in the ragged \r\ncontinental army?\r\nTo avoid the draft!');
