INSERT INTO jokes VALUES (5320, 'Halloween jokes', 'At 5 P.M. one Halloween afternoon, my dental \r\n\r\nhygienist realized that she wouldn''t make it to the store in time \r\nto \r\nget snacks for trick-or-treaters. So she took home some free \r\nsamples \r\nfrom the office supply cabinet. That night she handed out \r\ndozens of \r\ntoothbrushes, toothpaste, and dental floss. The next year, \r\nalthough she had \r\nbags of chips and popcorn, not one child came \r\nknocking at her \r\ndoor.');
INSERT INTO jokes VALUES (5321, 'Halloween jokes', 'Who turns the lights off at halloween \r\n?\r\nThe light''s witch !');
INSERT INTO jokes VALUES (5322, 'Halloween jokes', 'Q: What''s Osama Bin Laden going to be for \r\n\r\nHalloween? \r\nA: Dead.');
