INSERT INTO jokes VALUES (5998, 'Insect jokes', 'What did one flea say \r\nto the other after a \r\nnight out ?\r\nShall we walk home or take a dog ?');
INSERT INTO jokes VALUES (5999, 'Insect jokes', 'What is a flea''s favourite book ?\r\nThe \r\nitch-hikers guide to the galaxy !');
