INSERT INTO jokes VALUES (6001, 'Insect jokes', 'What insect runs away from everything ?\r\nA \r\nflee !');
INSERT INTO jokes VALUES (6002, 'Insect jokes', 'What is the difference between a flea bitten \r\ndog and a bored \r\nvisitor ?\r\nOnes going to itch and the other is \r\nitching to go !');
