INSERT INTO jokes VALUES (7498, 'Mouse jokes', 'What''s the hardest part of milking a mouse ? \r\n\r\nGetting it to fit over a bucket !');
INSERT INTO jokes VALUES (7499, 'Mouse jokes', 'Hickory hickory dock.\r\nThe mouse ran up the \r\nclock\r\nThe clock struck one\r\nBut the rest got away with minor \r\ninjuries');
